import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const [address, setAddress] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }

        const cartResponse = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        const addressResponse = await axios.post("http://localhost:8000/medical_store_app/get_address/", {
          user_id: userId,
        });

        setCartItems(cartResponse.data.cart_items);
        setTotalPrice(cartResponse.data.total_price);
        setAddress(addressResponse.data.address || "");
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
  };

  const handlePayment = () => {
    if (!address.trim()) {
      alert("Please add your billing address.");
      return;
    }

    if (paymentType === "Card") {
      alert("Payment successful! Thank you for your purchase.");
    } else {
      alert("Order placed successfully with Cash on Delivery!");
    }
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <div style={styles.mainBox}>
        {/* Address Section */}
        <div style={styles.addressBox}>
          <h2 style={styles.subHeading}>Billing Address</h2>
          <textarea
            style={styles.textarea}
            value={address}
            disabled
          />
          <button style={styles.button} onClick={() => navigate("/add-address")}>
            Change Address
          </button>
        </div>

        {/* Payment and Cart Details Section */}
        <div style={styles.paymentBox}>
          <h2 style={styles.subHeading}>Cart Details</h2>
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.tableHeader}>Medicine</th>
                <th style={styles.tableHeader}>Quantity</th>
                <th style={styles.tableHeader}>Price</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={index}>
                  <td style={styles.tableData}>{item.medicine.name}</td>
                  <td style={styles.tableData}>{item.quantity}</td>
                  <td style={styles.tableData}>Rs.{item.medicine.price * item.quantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3 style={styles.total}>Total: Rs.{totalPrice}</h3>

          <h2 style={styles.subHeading}>Payment Details</h2>
          <div style={styles.paymentTypeContainer}>
            <label>
              <input
                type="radio"
                value="Card"
                checked={paymentType === "Card"}
                onChange={handlePaymentTypeChange}
              />
              Pay by Card
            </label>
            <label>
              <input
                type="radio"
                value="Cash"
                checked={paymentType === "Cash"}
                onChange={handlePaymentTypeChange}
              />
              Cash on Delivery
            </label>
          </div>
          <button style={styles.paymentButton} onClick={handlePayment}>
            {paymentType === "Card" ? "Confirm Payment" : "Place Order"}
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: { padding: "40px", fontFamily: "Arial, sans-serif" },
  heading: { textAlign: "center", fontSize: "2rem", marginBottom: "20px" },
  mainBox: { display: "flex", gap: "20px" },
  addressBox: {
    flex: 1,
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
  },
  paymentBox: {
    flex: 2,
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
  },
  subHeading: { fontSize: "1.5rem", marginBottom: "20px" },
  textarea: {
    width: "100%",
    height: "150px",
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    resize: "none",
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
};

export default Checkout;
