import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const PaymentPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [paymentStatus, setPaymentStatus] = useState('');
  const history = useHistory();

  // Assuming the user is authenticated and you have their ID stored in localStorage
  const userId = localStorage.getItem('userId'); // Replace with actual user ID retrieval method

  useEffect(() => {
    if (!userId) {
      // Redirect to login if no user ID is found
      history.push('/login');
      return;
    }

    // Fetch cart data using the user ID
    axios.get(`/api/cart/${userId}/`) // Pass user ID to API
      .then(response => {
        setCartItems(response.data.cartItems);
        setTotalAmount(response.data.totalAmount);
      })
      .catch(error => {
        console.error("Error fetching cart data:", error);
      });
  }, [userId, history]);

  const handlePayment = () => {
    const paymentData = {
      userId,       // Include user ID in payment data
      cartItems,
      totalAmount,
    };

    axios.post('/api/payment/', paymentData)
      .then(response => {
        setPaymentStatus('Payment successful!');
        // Redirect to confirmation page or show success message
      })
      .catch(error => {
        setPaymentStatus('Payment failed! Please try again.');
        console.error("Payment error:", error);
      });
  };

  return (
    <div className="payment-page">
      <h1>Payment Page</h1>

      <div className="cart-summary">
        <h2>Your Cart:</h2>
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              {item.name} - ${item.price} x {item.quantity}
            </li>
          ))}
        </ul>
        <div className="total">
          <strong>Total Amount: ${totalAmount}</strong>
        </div>
      </div>

      <div className="payment-action">
        <button onClick={handlePayment}>Proceed to Payment</button>
      </div>

      {paymentStatus && <p>{paymentStatus}</p>}
    </div>
  );
};

export default PaymentPage;
