import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../axios";

export default function ListMedicines() {
  const [medicines, setMedicines] = useState([]);
  const [isEdit, setIsEdit] = useState(false);
  const [formValues, setFormValues] = useState({});
  const [loading, setLoading] = useState(true);

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const submit = (e) => {
    e.preventDefault();
    if (isEdit) {
      editMedicine();
    } else {
      addMedicine();
    }
  };

  const addMedicine = async () => {
    const userId = sessionStorage.getItem("user_id");
    const newMedicineData = { ...formValues, user: userId };

    await axiosInstance
      .post(apiUrl + "/medical_store_app/medicine/", newMedicineData)
      .then((response) => {
        if (response.status === 201) {
          alert("Medicine added successfully!");
          getMedicines(); // Refresh list
          resetForm();
        }
      })
      .catch((error) => {
        console.error("Error adding medicine:", error);
      });
  };

  const editMedicine = async () => {
    const userId = sessionStorage.getItem("user_id");
    const updatedMedicineData = { ...formValues, user: userId };

    await axiosInstance
      .put(apiUrl + `/medical_store_app/medicine/${formValues.id}/`, updatedMedicineData)
      .then((response) => {
        if (response.status === 200) {
          alert("Medicine updated successfully!");
          getMedicines(); // Refresh list
          resetForm();
        }
      })
      .catch((error) => {
        console.error("Error updating medicine:", error);
      });
  };

  const resetForm = () => {
    setIsEdit(false);
    setFormValues({});
    document.getElementById("staticBackdrop").modal('hide'); // Hide modal after submission
  };

  useEffect(() => {
    getMedicines();
  }, []);

  const setEditRecord = (record) => {
    setIsEdit(true);
    setFormValues({ ...record });
  };

  const deleteMedicine = async (id) => {
    await axiosInstance
      .delete(apiUrl + `/medical_store_app/medicine/${id}/`)
      .then((response) => {
        if (response.status === 204) { // Check for 204 No Content response
          alert("Medicine deleted successfully!");
          getMedicines(); // Refresh list
        }
      })
      .catch((error) => {
        console.error("Error deleting medicine:", error);
      });
  };

  const getMedicines = async () => {
    await axiosInstance
      .get(apiUrl + `/medical_store_app/medicine/`) // Adjusted the endpoint to get all medicines
      .then((response) => {
        if (response.status === 200) {
          setMedicines(response.data);
          setLoading(false);
        }
      })
      .catch((error) => {
        if (error.response?.status === 401) {
          generateRefreshToken(error);
        } else {
          console.error("Error fetching medicines:", error);
        }
      });
  };

  return (
    <div>
      <div className="container m-5">
        <div className="mt-3">
          {loading ? (
            <p>Loading medicines...</p>
          ) : (
            <table className="table table-striped">
              <thead className="thead-dark">
                <tr>
                  <th>SL No</th>
                  <th>Medicine Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Description</th>
                  <th>Stock Quantity</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {medicines.map((medicine, i) => (
                  <tr key={`medicine_row_${i}`}>
                    <td>{i + 1}</td>
                    <td>{medicine.medicine_name}</td>
                    <td>{medicine.category}</td>
                    <td>{medicine.price}</td>
                    <td>{medicine.description}</td>
                    <td>{medicine.stock_quantity}</td>
                    <td>
                      <a
                        href="#"
                        onClick={() => setEditRecord(medicine)}
                        className="px-2 text-primary"
                        data-bs-toggle="modal"
                        data-bs-target="#staticBackdrop"
                      >
                        <i className="bi bi-pencil"></i>
                      </a>
                      <a
                        href="#"
                        onClick={() => deleteMedicine(medicine.id)}
                        className="text-danger"
                      >
                        <i className="bi bi-trash3"></i>
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      {/* Edit Medicine Modal */}
      <div
        className="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabIndex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="staticBackdropLabel">
                {isEdit ? "Edit Medicine" : "Add Medicine"}
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
                onClick={resetForm}
              ></button>
            </div>
            <div className="modal-body">
              <div className="container m-4">
                <form onSubmit={submit} method="post">
                  <div className="row mb-2">
                    <div className="col">
                      <label htmlFor="medicine_name" className="form-label">
                        Medicine Name *
                      </label>
                      <input
                        type="text"
                        name="medicine_name"
                        className="form-control"
                        id="medicine_name"
                        onChange={onChangeHandler}
                        value={formValues?.medicine_name || ""}
                      />
                    </div>
                    <div className="col">
                      <label htmlFor="category" className="form-label">
                        Category *
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        name="category"
                        id="category"
                        onChange={onChangeHandler}
                        value={formValues?.category || ""}
                      />
                    </div>
                  </div>

                  <div className="row mb-2">
                    <div className="col">
                      <label htmlFor="price" className="form-label">
                        Price *
                      </label>
                      <input
                        type="number"
                        name="price"
                        className="form-control"
                        id="price"
                        onChange={onChangeHandler}
                        value={formValues?.price || ""}
                      />
                    </div>
                    <div className="col">
                      <label htmlFor="stock_quantity" className="form-label">
                        Stock Quantity *
                      </label>
                      <input
                        type="number"
                        className="form-control"
                        name="stock_quantity"
                        id="stock_quantity"
                        onChange={onChangeHandler}
                        value={formValues?.stock_quantity || ""}
                      />
                    </div>
                  </div>

                  <div className="row mb-2">
                    <div className="col">
                      <label htmlFor="description" className="form-label">
                        Description
                      </label>
                      <textarea
                        className="form-control"
                        name="description"
                        id="description"
                        onChange={onChangeHandler}
                        value={formValues?.description || ""}
                      ></textarea>
                    </div>
                  </div>

                  <div className="row">
                    <div className="col">
                      <button className="btn btn-primary">
                        {isEdit ? "Edit Medicine" : "Add Medicine"}
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
