import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../../axios";

export default function AddEditMedicine({ onAddMedicine }) {
  const user_id = sessionStorage.getItem("user_id");

  // Initialize the form values
  const [formValues, setFormValues] = useState({
    category: "",
    name: "",
    description: "",
    price: "",
    stock_quantity: "",
  });

  const [medicineCategories, setMedicineCategories] = useState([]);

  useEffect(() => {
    getMedicineCategories();
  }, []);

  const getMedicineCategories = async () => {
    try {
      const response = await axiosInstance.get(apiUrl + "/medical_store_app/medicine/categories/");
      if (response.status === 200) {
        console.log(response.data);
        setMedicineCategories(response.data);
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        generateRefreshToken(error);
      } else {
        console.error("Error fetching categories:", error);
      }
    }
  };

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const submit = (e) => {
    e.preventDefault();
    addMedicine();
  };

  const addMedicine = async () => {
    const medicineData = { ...formValues, user: user_id };
    console.log("Submitting Medicine Data:", medicineData);

    try {
      // Fetch all medicines from the backend to check for duplicates
      const checkResponse = await axiosInstance.get(apiUrl + "/medical_store_app/medicine/");
      
      if (Array.isArray(checkResponse.data)) {
        // Check if the medicine already exists by name
        const existingMedicine = checkResponse.data.find(
          (medicine) => medicine.name === formValues.name
        );

        if (existingMedicine) {
          // If the medicine exists, update the stock quantity
          const updatedMedicineData = {
            ...existingMedicine,
            stock_quantity: existingMedicine.stock_quantity + parseInt(formValues.stock_quantity),
          };

          // Send the update request
          const response = await axiosInstance.put(
            apiUrl + `/medical_store_app/medicine/update/${existingMedicine.id}/`,
            updatedMedicineData,
            {
              headers: { "Content-Type": "application/json" },
            }
          );

          if (response.status === 200 || response.status === 201) {
            alert("Medicine stock updated successfully!");
            onAddMedicine(response.data); // Call the prop function to update the list
            // Reset form values after success
            setFormValues({
              category: "",
              name: "",
              description: "",
              price: "",
              stock_quantity: "",
            });
          }
        } else {
          // If the medicine does not exist, add a new one
          const response = await axiosInstance.post(
            apiUrl + "/medical_store_app/medicine/add_or_update/",
            medicineData,
            {
              headers: { "Content-Type": "application/json" },
            }
          );

          if (response.status === 200 || response.status === 201) {
            alert("Medicine added successfully!");
            onAddMedicine(response.data); // Call the prop function to update the list
            // Reset form values after success
            setFormValues({
              category: "",
              name: "",
              description: "",
              price: "",
              stock_quantity: "",
            });
          }
        }
      } else {
        console.error("Error: Response data is not an array.");
      }
    } catch (error) {
      console.error("Error adding or updating medicine:", error);
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>Add Medicines</h1>
      <form onSubmit={submit} method="post">
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="medicine_category" style={{ display: "block", marginBottom: "5px" }}>
            Medicine Category *
          </label>
          <select
            name="category"
            id="category"
            value={formValues.category}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
          >
            <option value="">Select</option>
            {medicineCategories.length > 0 ? (
              medicineCategories.map((category, i) => (
                <option value={category.id} key={i}>
                  {category.category_name}
                </option>
              ))
            ) : (
              <option value="">No categories available</option>
            )}
          </select>
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="name" style={{ display: "block", marginBottom: "5px" }}>
            Medicine Name *
          </label>
          <input
            type="text"
            name="name"
            id="name"
            value={formValues.name}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
            required
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="description" style={{ display: "block", marginBottom: "5px" }}>
            Description
          </label>
          <textarea
            name="description"
            id="description"
            value={formValues.description}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="price" style={{ display: "block", marginBottom: "5px" }}>
            Price *
          </label>
          <input
            type="number"
            name="price"
            id="price"
            value={formValues.price}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
            required
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="stock" style={{ display: "block", marginBottom: "5px" }}>
            Stock Quantity *
          </label>
          <input
            type="number"
            name="stock_quantity"
            id="stock_quantity"
            value={formValues.stock_quantity}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
            required
          />
        </div>

        <div>
          <button
            type="submit"
            style={{
              padding: "10px 20px",
              backgroundColor: "#ff9800",
              color: "white",
              borderRadius: "5px",
              border: "none",
            }}
          >
            Add Medicine
          </button>
        </div>
      </form>
    </div>
  );
}
