import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { cartItems, totalPrice } = location.state || { cartItems: [], totalPrice: 0 };

  const handlePayment = () => {
    // Handle payment logic
    alert("Order placed successfully!");
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Price</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td style={styles.tableData}>{item.medicine.name}</td>
              <td style={styles.tableData}>{item.quantity}</td>
              <td style={styles.tableData}>Rs.{item.medicine.price * item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: Rs.{totalPrice}</h3>
      <button style={styles.paymentButton} onClick={handlePayment}>
        Proceed to Payment
      </button>
    </div>
  );
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  tableData: {
    padding: "10px",
    borderBottom: "1px solid #ddd",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Checkout;
