import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Booking = () => {
  const [bookings, setBookings] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0); 
  const navigate = useNavigate();
  const userId = sessionStorage.getItem("user_id");

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8000/medical_store_app/bookings/${userId}/`
        );
        const bookingData = response.data.bookings;

        setBookings(bookingData);

        // Calculate the total price
        const total = bookingData.reduce(
          (sum, booking) => sum + booking.product.price * booking.quantity,
          0
        );
        setTotalPrice(total);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, [userId]);

  const handleBackToHome = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <button style={styles.backButton} onClick={handleBackToHome}>
        Back to Shopping
      </button>
      <h1 style={styles.heading}>Your Bookings</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Price</th>
            <th style={styles.tableHeader}>Status</th>
            <th style={styles.tableHeader}>Booking Date</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking, index) => (
            <tr key={index}>
              <td>{booking.product.name}</td>
              <td>{booking.quantity}</td>
              <td>${(booking.product.price * booking.quantity).toFixed(2)}</td>
              <td>{booking.status}</td>
              <td>{new Date(booking.booking_date).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* <h3 style={styles.total}>Total Price: ${totalPrice.toFixed(2)}</h3> */}
    </div>
  );
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  backButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
};

export default Booking;
