import React, { useState, useMemo, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const user_data = useMemo(() => ({ user_id: sessionStorage.getItem("user_id") }), []);

  // Fetch cart items from backend
  const fetchCart = useCallback(async () => {
    if (!user_data.user_id) return;
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data
      );
      const { cart_items, total_price } = response.data;
      setCartItems(cart_items || []);
      setTotalPrice(total_price || 0);
    } catch (error) {
      setErrorMessage("Failed to fetch cart data.");
      const localCart = JSON.parse(localStorage.getItem("cart")) || [];
      const localTotalPrice = localStorage.getItem("totalPrice") || 0;
      setCartItems(localCart);
      setTotalPrice(localTotalPrice);
    }
  }, [user_data]);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleRemoveFromCart = async (cartId) => {
    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem("user_id")}`
      );
      fetchCart();
    } catch (error) {
      setErrorMessage("Failed to remove item from cart.");
    }
  };

  // Handle file upload for prescription
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("prescription_file", file);
    formData.append("user", sessionStorage.getItem("user_id"));

    const token = sessionStorage.getItem("auth_token");  // Get the token from sessionStorage

    if (!token) {
      setErrorMessage("Authorization token is missing.");
      return;
    }

    try {
      await axios.post(
        "http://localhost:8000/medical_store_app/upload_prescription/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            "Authorization": `Bearer ${token}`,  // Send the token in the Authorization header
          },
        }
      );
      setIsFileUploaded(true);
      setErrorMessage("");  // Clear error message on successful upload
    } catch (error) {
      setErrorMessage("Failed to upload prescription.");
    }
  };

  // Proceed to checkout
  const handleProceedToCheckout = async () => {
    if (!isFileUploaded) {
      setErrorMessage("Please upload the prescription file first.");
      return;
    }
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/create_order/",
        user_data
      );
      setCartItems([]);
      setTotalPrice(0);
      navigate("/checkout", {
        state: { order: response.data.order, totalPrice: totalPrice },
      });
    } catch (error) {
      setErrorMessage("Failed to create order.");
    }
  };

  // Handle continue shopping
  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Continue Shopping button */}
      <button
        style={{ padding: "10px 20px", marginBottom: "20px", cursor: "pointer" }}
        onClick={handleContinueShopping}
      >
        Continue Shopping
      </button>

      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>Your Cart</h1>

      {/* Error message display */}
      {errorMessage && (
        <div style={{ color: "red", marginBottom: "20px", textAlign: "center" }}>
          <strong>{errorMessage}</strong>
        </div>
      )}

      {/* Cart Items Table */}
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "20px" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Medicine</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Quantity</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.medicine.name}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{item.quantity}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <button
                  onClick={() => handleRemoveFromCart(item.id)}
                  style={{ padding: "5px 10px", cursor: "pointer" }}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Total Price */}
      <h3 style={{ textAlign: "right", marginBottom: "20px" }}>Total: ₹{totalPrice}</h3>

      {/* Prescription File Upload */}
      <div style={{ marginBottom: "20px" }}>
        <label htmlFor="prescription" style={{ marginRight: "10px" }}>
          Upload Prescription:
        </label>
        <input
          type="file"
          id="prescription"
          onChange={handleFileUpload}
          style={{ padding: "5px" }}
        />
      </div>

      {/* Proceed to Booking Button */}
      <button
        style={{
          padding: "10px 20px",
          cursor: isFileUploaded ? "pointer" : "not-allowed",
          opacity: isFileUploaded ? 1 : 0.5,
        }}
        onClick={handleProceedToCheckout}
        disabled={!isFileUploaded}
      >
        Proceed to Booking
      </button>
    </div>
  );
};

export default Cart;
