import React, { useState } from 'react';
import axios from 'axios';

const UpdateStockQuantity = () => {
  const [medicineId, setMedicineId] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleUpdate = async () => {
    setMessage('');
    setError('');
    const token = localStorage.getItem('token');

    if (!token) {
      setError("You are not logged in. Please log in to update stock.");
      return;
    }

    if (!medicineId || !stockQuantity) {
      setError("Medicine ID and Stock Quantity are required.");
      return;
    }

    try {
      const response = await axios.patch(
        `http://localhost:8000/medical_store_app/medicine/update-stock/${medicineId}/`,
        { stock_quantity: stockQuantity },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      setMessage(response.data.message || "Stock quantity updated successfully!");
    } catch (error) {
      if (error.response) {
        setError(
          error.response.data.error || "Failed to update stock quantity. Please try again."
        );
      } else {
        setError("An unexpected error occurred. Please check your network connection.");
      }
    }
  };

  return (
    <div>
      <h1>Update Stock Quantity</h1>
      <div>
        <label>Medicine ID:</label>
        <input
          type="text"
          value={medicineId}
          onChange={(e) => setMedicineId(e.target.value)}
          placeholder="Enter Medicine ID"
        />
      </div>
      <div>
        <label>Stock Quantity:</label>
        <input
          type="number"
          value={stockQuantity}
          onChange={(e) => setStockQuantity(e.target.value)}
          placeholder="Enter Stock Quantity"
        />
      </div>
      <button onClick={handleUpdate}>Update Stock</button>
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default UpdateStockQuantity;
