import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const API_BASE_URL = "http://localhost:8000/medical_store_app/api";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCartItems(); // Fetch cart items on component mount
  }, []);

  // Fetch cart items
  const fetchCartItems = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/cart/`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
      });
      setCartItems(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart items:", error);
      setLoading(false);
    }
  };

  // Remove item from cart
  const removeFromCart = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/cart/${id}/`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
      });
      setCartItems((prevItems) => prevItems.filter((item) => item.id !== id));
    } catch (error) {
      console.error("Error removing item from cart:", error);
    }
  };

  if (loading) return <p>Loading cart items...</p>;

  return (
    <div className="cart-page">
      <header className="navbar">
        <h1>Your Cart</h1>
        <Link to="/" className="nav-button">Back to Home</Link>
      </header>

      {cartItems.length > 0 ? (
        <div className="cart-list">
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <h3>{item.medicine.name}</h3>
              <p>Price: ${item.medicine.price}</p>
              <p>Quantity: {item.quantity}</p>
              <button
                className="remove-button"
                onClick={() => removeFromCart(item.id)}
              >
                Remove
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
}


// Inline CSS styles
const styles = {
  cartPage: {
    maxWidth: "600px",
    margin: "2em auto",
    padding: "1em",
    background: "#fff",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  cartTitle: {
    fontSize: "24px",
    marginBottom: "1em",
    textAlign: "center",
  },
  emptyCart: {
    textAlign: "center",
    fontSize: "16px",
  },
  cartList: {
    listStyle: "none",
    padding: "0",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0.5em 0",
    borderBottom: "1px solid #ddd",
  },
  medicineName: {
    fontSize: "18px",
  },
  price: {
    fontSize: "16px",
  },
  quantity: {
    fontSize: "16px",
  },
  removeButton: {
    padding: "5px 10px",
    color: "#fff",
    backgroundColor: "#e74c3c",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  cartTotal: {
    marginTop: "1em",
    fontSize: "18px",
    textAlign: "right",
  },
  checkoutButton: {
    padding: "10px 20px",
    color: "#fff",
    backgroundColor: "#2ecc71",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};
