import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

// Load Stripe outside of a component’s render to avoid recreating the object on every render
const stripePromise = loadStripe('your_stripe_publishable_key');

const PaymentForm = () => {
  const [clientSecret, setClientSecret] = useState('');
  const [amount, setAmount] = useState(0); // Default amount will be set from the cart
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);

  const stripe = useStripe();
  const elements = useElements();

  // Get the passed totalPrice from the Cart page
  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  // Set the total amount to the price passed from the cart
  useEffect(() => {
    setAmount(totalPrice * 100); // Convert to cents
  }, [totalPrice]);

  useEffect(() => {
    const fetchClientSecret = async () => {
      const response = await fetch('/create-payment-intent/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount }),
      });

      if (response.ok) {
        const data = await response.json();
        setClientSecret(data.clientSecret);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Failed to create payment intent');
      }
    };

    if (amount > 0) {
      fetchClientSecret();
    }
  }, [amount]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!stripe || !elements || !clientSecret) {
      return;
    }

    setProcessing(true);

    const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (stripeError) {
      setError(stripeError.message);
    } else if (paymentIntent.status === 'succeeded') {
      alert('Payment successful!');
    }
    setProcessing(false);
  };

  // Inline CSS Styles
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginTop: '50px',
      fontFamily: 'Arial, sans-serif',
      width: '100%',
      maxWidth: '500px',
      margin: '0 auto',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      fontSize: '24px',
      marginBottom: '20px',
    },
    label: {
      marginBottom: '10px',
      fontSize: '16px',
      fontWeight: 'bold',
    },
    input: {
      marginBottom: '20px',
      padding: '10px',
      fontSize: '16px',
      borderRadius: '5px',
      border: '1px solid #ddd',
      width: '100%',
    },
    button: {
      padding: '10px 20px',
      fontSize: '16px',
      backgroundColor: '#4CAF50',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    },
    buttonDisabled: {
      backgroundColor: '#ccc',
      cursor: 'not-allowed',
    },
    error: {
      color: 'red',
      fontSize: '14px',
      marginTop: '10px',
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Amount:</label>
        <input
          type="number"
          value={amount / 100}
          readOnly
          style={styles.input}
        />
        <CardElement />
        <button
          type="submit"
          disabled={!stripe || processing || !clientSecret}
          style={processing || !stripe || !clientSecret ? { ...styles.button, ...styles.buttonDisabled } : styles.button}
        >
          {processing ? 'Processing...' : 'Pay'}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};

const PaymentPage = () => (
  <Elements stripe={stripePromise}>
    <PaymentForm />
  </Elements>
);

export default PaymentPage;
