import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const [address, setAddress] = useState("");
  const [addressChanged, setAddressChanged] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }
        const cartResponse = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        if (cartResponse.data && cartResponse.data.cart_items) {
          setCartItems(cartResponse.data.cart_items);
          setTotalPrice(cartResponse.data.total_price);
        } else {
          console.error("Invalid cart data:", cartResponse.data);
        }

        const addressResponse = await axios.get(`http://localhost:8000/medical_store_app/get_address/${userId}`);
        if (addressResponse.data) {
          const { street, city, state, postal_code } = addressResponse.data;
          const fullAddress = `${street}, ${city}, ${state} - ${postal_code}`;
          setAddress(fullAddress);
        } else {
          console.error("No address found for user:", userId);
          setAddress("");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        setAddress("");
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
  };

  const saveAddress = async (addressData) => {
    try {
      const userId = sessionStorage.getItem("user_id");
      const response = await axios.post("http://localhost:8000/medical_store_app/add_address/", {
        user_id: userId,
        ...addressData,
      });
      if (response.status === 200) {
        alert("Address saved successfully!");
        const { street, city, state, postal_code } = addressData;
        const fullAddress = `${street}, ${city}, ${state} - ${postal_code}`;
        setAddress(fullAddress);
        setAddressChanged(false);
      } else {
        alert("Failed to save the address.");
      }
    } catch (error) {
      console.error("Error saving address:", error);
      alert("An error occurred while saving your address.");
    }
  };

  const handlePayment = () => {
    if (!address.trim()) {
      alert("Please add your billing address.");
      return;
    }

    if (paymentType === "Card") {
      alert("Payment successful! Thank you for your purchase.");
    } else {
      alert("Order placed successfully with Cash on Delivery!");
    }
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <div style={styles.mainBox}>
  
        <div style={styles.addressBox}>
          <h2 style={styles.subHeading}>Billing Address</h2>
          {address.trim() ? (
            <div>
              <textarea
                style={styles.textarea}
                value={address}
                disabled={!addressChanged}
                onChange={(e) => setAddress(e.target.value)}
              />
              <button
                style={styles.button}
                onClick={() => setAddressChanged(true)}
              >
                Change Address
              </button>
              {addressChanged && (
                <button
                  style={styles.button}
                  onClick={() =>
                    saveAddress({
                      street: address.split(",")[0].trim(),
                      city: address.split(",")[1].trim(),
                      state: address.split(",")[2].split("-")[0].trim(),
                      postal_code: address.split("-")[1].trim(),
                    })
                  }
                >
                  Save Address
                </button>
              )}
            </div>
          ) : (
            <div>
              <p>Please add your billing address.</p>
              <button onClick={() => navigate("/addaddress")}>Add Address</button>
            </div>
          )}
        </div>


        <div style={styles.paymentBox}>
          <h2 style={styles.subHeading}>Cart Details</h2>
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.tableHeader}>Medicine</th>
                <th style={styles.tableHeader}>Quantity</th>
                <th style={styles.tableHeader}>Price</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={index}>
                  <td style={styles.tableData}>{item.medicine?.name || "Unknown Medicine"}</td>
                  <td style={styles.tableData}>{item.quantity || 0}</td>
                  <td style={styles.tableData}>
                    Rs.
                    {item.medicine?.price && item.quantity
                      ? item.medicine.price * item.quantity
                      : 0}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3 style={styles.total}>Total: Rs.{totalPrice}</h3>

          <h2 style={styles.subHeading}>Payment Details</h2>
          <div style={styles.paymentTypeContainer}>
            <label>
              <input
                type="radio"
                value="Card"
                checked={paymentType === "Card"}
                onChange={handlePaymentTypeChange}
              />
              Pay by Card
            </label>
            <label>
              <input
                type="radio"
                value="Cash"
                checked={paymentType === "Cash"}
                onChange={handlePaymentTypeChange}
              />
              Cash on Delivery
            </label>
          </div>
          <button style={styles.paymentButton} onClick={handlePayment}>
            {paymentType === "Card" ? "Confirm Payment" : "Place Order"}
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: { padding: "40px", fontFamily: "Arial, sans-serif" },
  heading: { textAlign: "center", fontSize: "2rem", marginBottom: "20px" },
  mainBox: { display: "flex", gap: "20px" },
  addressBox: {
    flex: 1,
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
  },
  paymentBox: {
    flex: 2,
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
  },
  subHeading: { fontSize: "1.5rem", marginBottom: "20px" },
  textarea: {
    width: "100%",
    height: "150px",
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    resize: "none",
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
  table: { width: "100%", borderCollapse: "collapse", marginTop: "20px" },
  tableHeader: {
    borderBottom: "2px solid #ddd",
    padding: "10px",
    textAlign: "left",
  },
  tableData: { padding: "10px", borderBottom: "1px solid #ddd" },
  total: { textAlign: "right", marginTop: "20px", fontSize: "1.2rem" },
  paymentTypeContainer: { display: "flex", flexDirection: "column", gap: "10px" },
  paymentButton: {
    marginTop: "20px",
    backgroundColor: "#28a745",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Checkout;
