import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import Navbar from '../Admin/Navbar'; // Import the Navbar component
import handleAddToCart from '../Admin/cart'; 

const UserProductViews = () => {
    const [medicines, setMedicines] = useState([]); // State to hold medicines
    const [cart, setCart] = useState([]); // State to hold cart items
    const navigate = useNavigate(); // Initialize navigate function

    const fetchMedicines = async () => {
        try {
            const response = await fetch('http://localhost:8000/medical_store_app/api/medicine/');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setMedicines(data); // Store fetched data in state
            console.log('Fetched Medicines:', data); // Debugging line
        } catch (error) {
            console.error('Error fetching medicines:', error);
        }
    };

    useEffect(() => {
        fetchMedicines(); // Fetch medicines on component mount
    }, []);

    // Handle adding medicine to the cart
    const handleAddToCart = (medicine) => {
        setCart((prevCart) => [...prevCart, medicine]); // Add medicine to the cart
        console.log('Added to cart:', medicine);
    };

    // Handle Buy Now (navigate to cart)
    const handleBuyNow = (medicine) => {
        // Add the medicine to the cart
        setCart((prevCart) => [...prevCart, medicine]);
        console.log('Buying now:', medicine);
        // Redirect to the cart page
        navigate('/cart');
    };

    return (
        <div style={styles.container}>
            <Navbar cartCount={cart.length} /> {/* Display cart count in Navbar */}
            <h1 style={styles.title}>Available Medicines</h1>
            {medicines.length === 0 ? (
                <p style={styles.noMedicines}>No medicines found.</p>
            ) : (
                <div style={styles.productsContainer}>
                    {medicines.map((medicine) => (
                        <div key={medicine.id} style={styles.productCard}>
                            <h3 style={styles.productName}>{medicine.name}</h3>
                            <p style={styles.productDescription}>{medicine.description}</p>
                            <p style={styles.productPrice}>Price: ${medicine.price}</p>
                            <div>
                                <button
                                    onClick={() => Ha(medicine)}
                                    style={styles.addToCartButton}
                                >
                                    Add to Cart
                                </button>
                                <button
                                    onClick={() => handleBuyNow(medicine)} // Navigate to cart on Buy Now
                                    style={styles.buyNowButton}
                                >
                                    Buy Now
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

const styles = {
    container: {
        padding: '80px',
    },
    title: {
        textAlign: 'center',
        marginBottom: '20px',
    },
    noMedicines: {
        textAlign: 'center',
        fontSize: '18px',
        color: '#555',
    },
    productsContainer: {
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px',
        justifyContent: 'center',
    },
    productCard: {
        border: '1px solid #ddd',
        borderRadius: '8px',
        padding: '20px',
        width: '200px',
        textAlign: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        marginBottom: '20px',
        transition: 'transform 0.3s ease-in-out',
    },
    productCardHover: {
        transform: 'scale(1.05)',
    },
    productName: {
        marginBottom: '10px',
        fontSize: '18px',
        fontWeight: 'bold',
        color: '#333',
    },
    productDescription: {
        marginBottom: '10px',
        color: '#777',
        fontSize: '14px',
    },
    productPrice: {
        marginBottom: '10px',
        fontSize: '16px',
        fontWeight: 'bold',
        color: '#28a745',
    },
    addToCartButton: {
        backgroundColor: '#28a745',
        color: 'white',
        padding: '10px 15px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        marginRight: '10px',
        transition: 'background-color 0.3s ease',
    },
    addToCartButtonHover: {
        backgroundColor: '#218838',
    },
    buyNowButton: {
        backgroundColor: '#007bff',
        color: 'white',
        padding: '10px 15px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background-color 0.3s ease',
    },
    buyNowButtonHover: {
        backgroundColor: '#0056b3',
    },
};

export default UserProductViews;
