import React, { useEffect, useState, useCallback, useMemo } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [isLoading, setIsLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error state
  const navigate = useNavigate();

  const user_data = useMemo(
    () => ({ user_id: sessionStorage.getItem("user_id") }),
    []
  );

  const fetchCart = useCallback(async () => {
    if (!user_data.user_id) {
      console.error("No user ID found in session.");
      return;
    }
    setIsLoading(true);
    setError("");

    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data
      );

      const { cart_items, total_price } = response.data;
      setCartItems(cart_items || []);
      setTotalPrice(total_price || 0);

      // Save to localStorage as a backup
      localStorage.setItem("cart", JSON.stringify(cart_items));
      localStorage.setItem("totalPrice", total_price);
    } catch (err) {
      setError("Failed to fetch cart items. Please try again.");
      console.error("Error fetching cart items:", err);

      // Load local backup on failure
      const localCart = JSON.parse(localStorage.getItem("cart")) || [];
      const localTotalPrice = localStorage.getItem("totalPrice") || 0;

      setCartItems(localCart);
      setTotalPrice(localTotalPrice);
    } finally {
      setIsLoading(false);
    }
  }, [user_data]);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleRemoveFromCart = async (cartId) => {
    setIsLoading(true);
    setError("");
    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem(
          "user_id"
        )}`
      );
      fetchCart();
    } catch (err) {
      setError("Failed to remove item from cart. Please try again.");
      console.error("Error removing cart item:", err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleProceedToCheckout = async () => {
    setIsLoading(true);
    setError("");
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/create_order/",
        user_data
      );

      setCartItems([]);
      setTotalPrice(0);
      localStorage.removeItem("cart");
      localStorage.removeItem("totalPrice");

      navigate("/checkout", {
        state: { order: response.data.order, totalPrice: totalPrice },
      });
    } catch (err) {
      setError("Failed to place order. Please try again.");
      console.error("Error booking order:", err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log("File uploaded:", file.name);
      setIsFileUploaded(true);
    }
  };

  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      {isLoading && <p style={styles.loading}>Loading...</p>}
      {error && <p style={styles.error}>{error}</p>}
      <button
        style={styles.continueShoppingButton}
        onClick={handleContinueShopping}
      >
        Continue Shopping
      </button>
      <h1 style={styles.heading}>Your Cart</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td>{item.medicine.name}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleRemoveFromCart(item.id)}
                  style={styles.button}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: â‚¹{totalPrice}</h3>
      <div style={styles.uploadContainer}>
        <label htmlFor="prescription" style={styles.uploadLabel}>
          Upload Prescription:
        </label>
        <input
          type="file"
          id="prescription"
          onChange={handleFileUpload}
          style={styles.fileInput}
        />
      </div>
      <div style={styles.bookContainer}>
        <button
          style={{
            ...styles.paymentButton,
            opacity: isFileUploaded ? 1 : 0.5,
            cursor: isFileUploaded ? "pointer" : "not-allowed",
          }}
          onClick={handleProceedToCheckout}
          disabled={!isFileUploaded}
        >
          Proceed to Booking
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  loading: {
    color: "blue",
    textAlign: "center",
    margin: "10px 0",
  },
  error: {
    color: "red",
    textAlign: "center",
    margin: "10px 0",
  },
  continueShoppingButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  button: {
    backgroundColor: "#ff4d4d",
    color: "white",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
  bookContainer: {
    display: "flex",
    justifyContent: "center",
    marginTop: "20px",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  uploadContainer: {
    marginTop: "20px",
    textAlign: "center",
  },
  uploadLabel: {
    marginRight: "10px",
    fontSize: "1rem",
  },
  fileInput: {
    fontSize: "1rem",
  },
};

export default Cart;
