import React, { useEffect, useState } from 'react';

// Example inline CSS styles
const styles = {
    container: {
        padding: '20px',
    },
    searchInput: {
        width: '100%',
        padding: '10px',
        marginBottom: '20px',
    },
    productCard: {
        border: '1px solid #ccc',
        borderRadius: '5px',
        padding: '10px',
        margin: '10px 0',
    },
    addButton: {
        padding: '10px 15px',
        backgroundColor: '#28a745',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
    },
    cartButton: {
        marginTop: '20px',
        padding: '10px 15px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
    }
};

const UserProductViews = () => {
    const [medicines, setMedicines] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [cart, setCart] = useState([]);

    useEffect(() => {
        const fetchMedicines = async () => {
            try {
                const response = await fetch('http://localhost:8000/api/medicines/');
                const data = await response.json();
                setMedicines(data); // Ensure your API returns the list correctly
            } catch (error) {
                console.error('Error fetching medicines:', error);
            }
        };

        fetchMedicines();
    }, []);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleAddToCart = (medicine) => {
        setCart([...cart, medicine]);
    };

    const filteredMedicines = medicines.filter((medicine) =>
        medicine.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div style={styles.container}>
            <input
                type="text"
                placeholder="Search medicines..."
                style={styles.searchInput}
                value={searchTerm}
                onChange={handleSearch}
            />

            {filteredMedicines.length > 0 ? (
                filteredMedicines.map((medicine) => (
                    <div key={medicine.id} style={styles.productCard}>
                        <h4>{medicine.name}</h4>
                        <p>{medicine.description}</p>
                        <button
                            style={styles.addButton}
                            onClick={() => handleAddToCart(medicine)}
                        >
                            Add to Cart
                        </button>
                    </div>
                ))
            ) : (
                <p>No medicines found.</p>
            )}

            {cart.length > 0 && (
                <button
                    style={styles.cartButton}
                    onClick={() => console.log('Cart:', cart)}
                >
                    View Cart ({cart.length})
                </button>
            )}
        </div>
    );
};

export default UserProductViews;
