import React, { Component } from "react";
import APIHandler from "./APIHandler"; // Import the APIHandler
import AuthHandler from "../utils/AuthHandler"; // Import the AuthHandler
import { reactLocalStorage } from "reactjs-localstorage";

class MedicineManage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      companylist: [],
      medicineDataList: [],
      dataLoaded: false,
      selectedMedicine: null,
      editMode: false, // To toggle between add/edit modes
    };

    this.apiHandler = new APIHandler();
  }

  // Load initial data when component mounts
  async componentDidMount() {
    await this.LoadInitialData();
  }

  // Load companies and medicines from API
  async LoadInitialData() {
    try {
      const companydata = await this.apiHandler.fetchCompanyOnly();
      const medicinedata = await this.apiHandler.fetchAllMedicine();
      this.setState({
        companylist: companydata.data,
        medicineDataList: medicinedata.data,
        dataLoaded: true,
      });
    } catch (error) {
      console.error("Error loading initial data:", error);
    }
  }

  // Handle form input change
  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState((prevState) => ({
      selectedMedicine: {
        ...prevState.selectedMedicine,
        [name]: value,
      },
    }));
  };

  // Handle edit button click
  handleEditClick = (medicine) => {
    this.setState({
      selectedMedicine: medicine,
      editMode: true,
    });
  };

  // Handle add/edit form submission
  handleFormSubmit = async (event) => {
    event.preventDefault();
    const {
      name,
      medical_typ,
      buy_price,
      sell_price,
      c_gst,
      s_gst,
      batch_no,
      shelf_no,
      expire_date,
      mfg_date,
      company_id,
      description,
      in_stock_total,
      qty_in_strip,
      medicine_details,
      id, // Only used in edit mode
    } = this.state.selectedMedicine;

    if (this.state.editMode) {
      // Edit existing medicine data
      try {
        const response = await this.apiHandler.editMedicineData(
          name,
          medical_typ,
          buy_price,
          sell_price,
          c_gst,
          s_gst,
          batch_no,
          shelf_no,
          expire_date,
          mfg_date,
          company_id,
          description,
          in_stock_total,
          qty_in_strip,
          medicine_details,
          id
        );
        if (response.error) {
          alert(response.message);
        } else {
          alert("Medicine updated successfully");
          this.setState({ editMode: false, selectedMedicine: null });
          this.LoadInitialData(); // Reload the medicine data
        }
      } catch (error) {
        console.error("Error updating medicine:", error);
      }
    } else {
      // Logic for adding a new medicine can go here
      // Assuming an `addMedicine` method exists in APIHandler
    }
  };

  render() {
    if (!this.state.dataLoaded) {
      return <div>Loading...</div>;
    }

    return (
      <div>
        <h2>Manage Medicines</h2>
        {/* Medicine list table */}
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.state.medicineDataList.map((medicine) => (
              <tr key={medicine.id}>
                <td>{medicine.name}</td>
                <td>{medicine.medical_typ}</td>
                <td>{medicine.sell_price}</td>
                <td>
                  <button onClick={() => this.handleEditClick(medicine)}>
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Medicine form for editing or adding a new medicine */}
        {this.state.editMode && (
          <form onSubmit={this.handleFormSubmit}>
            <h3>{this.state.editMode ? "Edit Medicine" : "Add Medicine"}</h3>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={this.state.selectedMedicine.name}
              onChange={this.handleInputChange}
              required
            />
            <label>Type:</label>
            <input
              type="text"
              name="medical_typ"
              value={this.state.selectedMedicine.medical_typ}
              onChange={this.handleInputChange}
              required
            />
            <label>Buy Price:</label>
            <input
              type="number"
              name="buy_price"
              value={this.state.selectedMedicine.buy_price}
              onChange={this.handleInputChange}
              required
            />
            <label>Sell Price:</label>
            <input
              type="number"
              name="sell_price"
              value={this.state.selectedMedicine.sell_price}
              onChange={this.handleInputChange}
              required
            />
            <label>CGST:</label>
            <input
              type="number"
              name="c_gst"
              value={this.state.selectedMedicine.c_gst}
              onChange={this.handleInputChange}
              required
            />
            <label>SGST:</label>
            <input
              type="number"
              name="s_gst"
              value={this.state.selectedMedicine.s_gst}
              onChange={this.handleInputChange}
              required
            />
            <label>Batch No:</label>
            <input
              type="text"
              name="batch_no"
              value={this.state.selectedMedicine.batch_no}
              onChange={this.handleInputChange}
              required
            />
            <label>Shelf No:</label>
            <input
              type="text"
              name="shelf_no"
              value={this.state.selectedMedicine.shelf_no}
              onChange={this.handleInputChange}
              required
            />
            <label>Expire Date:</label>
            <input
              type="date"
              name="expire_date"
              value={this.state.selectedMedicine.expire_date}
              onChange={this.handleInputChange}
              required
            />
            <label>Mfg Date:</label>
            <input
              type="date"
              name="mfg_date"
              value={this.state.selectedMedicine.mfg_date}
              onChange={this.handleInputChange}
              required
            />
            <label>Company:</label>
            <select
              name="company_id"
              value={this.state.selectedMedicine.company_id}
              onChange={this.handleInputChange}
              required
            >
              {this.state.companylist.map((company) => (
                <option key={company.id} value={company.id}>
                  {company.name}
                </option>
              ))}
            </select>
            <label>Description:</label>
            <textarea
              name="description"
              value={this.state.selectedMedicine.description}
              onChange={this.handleInputChange}
            />
            <label>In Stock Total:</label>
            <input
              type="number"
              name="in_stock_total"
              value={this.state.selectedMedicine.in_stock_total}
              onChange={this.handleInputChange}
              required
            />
            <label>Quantity in Strip:</label>
            <input
              type="number"
              name="qty_in_strip"
              value={this.state.selectedMedicine.qty_in_strip}
              onChange={this.handleInputChange}
              required
            />
            <label>Medicine Details:</label>
            <textarea
              name="medicine_details"
              value={this.state.selectedMedicine.medicine_details}
              onChange={this.handleInputChange}
            />
            <button type="submit">Submit</button>
          </form>
        )}
      </div>
    );
  }
}

export default MedicineManage;
