import React from "react";
import { useLocation } from "react-router-dom";

const Booking = () => {
  const location = useLocation();
  const { order = {}, paymentDetails = {} } = location.state || {}; // Add default values

  const containerStyle = {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    width: "80%",
    margin: "20px auto",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  };

  const headingStyle = {
    fontSize: "24px",
    color: "#333",
    textAlign: "center",
    marginBottom: "20px",
  };

  const detailStyle = {
    fontSize: "18px",
    color: "#555",
    margin: "10px 0",
  };

  const paymentHeadingStyle = {
    fontSize: "20px",
    color: "#444",
    marginTop: "20px",
  };

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Booking Details</h2>
      <p style={detailStyle}>Order ID: {order.id || "N/A"}</p>
      <p style={detailStyle}>Total Price: ₹{order.totalPrice || "N/A"}</p>
      <h3 style={paymentHeadingStyle}>Payment Details:</h3>
      <p style={detailStyle}>
        Payment ID: {paymentDetails.razorpay_payment_id || "N/A"}
      </p>
      <p style={detailStyle}>
        Order ID: {paymentDetails.razorpay_order_id || "N/A"}
      </p>
      <p style={detailStyle}>
        Amount Paid: ₹
        {paymentDetails.amount
          ? (paymentDetails.amount / 100).toFixed(2)
          : "N/A"}
      </p>
      <p style={detailStyle}>
        Signature: {paymentDetails.razorpay_signature || "N/A"}
      </p>
    </div>
  );
};

export default Booking;
