import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios";
import { jwtDecode } from "jwt-decode";

export default function Login() {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({});
  const [formError, setFormError] = useState("");
  const [loading, setLoading] = useState(false); // Define the loading state

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    setLoading(true); // Set loading state to true

    console.log("Form Values:", formValues); // Log form values for debugging
    const loginUrl = apiUrl + "/api/token/"; // Ensure this URL is correct
    console.log("Making POST request to:", loginUrl); // Log the request URL

    axios
      .post(loginUrl, formValues) // This should be a POST request
      .then((response) => {
        // Handle successful login
        sessionStorage.setItem("access_token", response.data.access);
        sessionStorage.setItem("refresh_token", response.data.refresh);

        const user = jwtDecode(response.data.access); // Decode the token
        const userId = user.user_id; // Get user ID
        const username = user.name; // Get username

        // Store user information in session storage
        sessionStorage.setItem("user_id", userId);
        sessionStorage.setItem("name", username);

        // Navigate to a default page after login
        navigate("/home");
      })
      .catch((error) => {
        setLoading(false); // Set loading state to false
        if (error.response && error.response.status === 401) {
          setFormError(error.response.data.detail);
        } else {
          setFormError("An error occurred. Please try again."); // General error handling
        }
      })
      .finally(() => {
        setLoading(false); // Ensure loading state is reset regardless of success or failure
      });
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)", // Creative background gradient
      }}
    >
      <div
        className="container mt-5"
        style={{
          width: "450px", // Increased width for a broader layout
          height: "auto",
          boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)", // Softer box shadow
          backgroundColor: "#ffffff", // White background for a cleaner look
          padding: "40px",
          borderRadius: "15px", // Rounded corners for a modern look
          border: "2px solid #007bff", // Add a border to align with the text
        }}
      >
        <form onSubmit={onSubmit} method="post" className="form">
          <div className="row mt-3 mb-3">
            <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff", textAlign: "center" }}>
              <i className="bi bi-person-plus px-3"></i>Login
            </h2>
          </div>

          {/* Username */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="username"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Username:
            </label>
            <input
              required
              type="text"
              name="username"
              id="username"
              placeholder="Enter your username"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={onChangeHandler}
            />
          </div>

          {/* Password */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="password"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Password:
            </label>
            <input
              required
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={onChangeHandler}
            />
          </div>

          {/* Error message */}
          <p className="text-danger" style={{ fontSize: "14px", marginBottom: "20px" }}>
            {formError}
          </p>

          {/* Registration link */}
          <p style={{ fontSize: "14px", textAlign: "center", marginBottom: "20px" }}>
            New User?{" "}
            <a href="register" style={{ color: "#007bff", textDecoration: "none" }}>
              Register here
            </a>
          </p>

          {/* Submit button */}
          <div className="row mb-3">
            <div className="col text-center">
              <button
                className="btn btn-dark"
                style={{
                  width: "50%",
                  fontSize: "18px",
                  padding: "10px 20px",
                  backgroundColor: "#007bff",
                  border: "none",
                  borderRadius: "8px",
                  margin: "0 auto",
                  textAlign: "center",
                }}
                disabled={loading} // Disable the button while loading
              >
                {loading ? "Logging in..." : "Login"}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
