import React, { useEffect, useState, useCallback, useMemo } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  const user_data = useMemo(
    () => ({ user_id: sessionStorage.getItem("user_id") }),
    []
  );

  const fetchCart = useCallback(async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data
      );
      setCartItems(response.data.cart_items);
      setTotalPrice(response.data.total_price);
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
  }, [user_data]);

  useEffect(() => {
    // Load Razorpay SDK dynamically
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.async = true;
    document.body.appendChild(script);

    fetchCart();

    return () => {
      document.body.removeChild(script);
    };
  }, [fetchCart]);

  const handleRemoveFromCart = async (cartId) => {
    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem(
          "user_id"
        )}`
      );
      fetchCart();
    } catch (error) {
      console.error("Error removing cart item:", error);
    }
  };

  const handleProceedToBooking = async () => {
    if (typeof Razorpay === "undefined") {
      console.error("Razorpay SDK is not loaded!");
      return;
    }

    try {
      // Get order details from backend
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/book_order/",
        { user_id: sessionStorage.getItem("user_id") }
      );

      const orderDetails = response.data.order;
      const options = {
        key: "your_razorpay_key", // Replace with your Razorpay key
        amount: orderDetails.amount * 100, // Amount in paise
        currency: "INR",
        name: "Medical Store",
        description: "Order Payment",
        order_id: orderDetails.id,
        handler: function (response) {
          console.log("Payment successful:", response);
          navigate("/Booking", { state: { order: orderDetails } });
        },
        prefill: {
          name: sessionStorage.getItem("user_name"),
          email: sessionStorage.getItem("user_email"),
          contact: sessionStorage.getItem("user_phone"),
        },
        theme: {
          color: "#4CAF50",
        },
      };

      const razorpay = new Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error("Error during payment:", error);
    }
  };

  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <button
        style={styles.continueShoppingButton}
        onClick={handleContinueShopping}
      >
        Continue Shopping
      </button>
      <h1 style={styles.heading}>Your Cart</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td>{item.medicine.name}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleRemoveFromCart(item.id)}
                  style={styles.button}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: ₹{totalPrice}</h3>
      <div style={styles.BookContainer}>
        <button style={styles.paymentButton} onClick={handleProceedToBooking}>
          Proceed to Booking
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  continueShoppingButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  button: {
    backgroundColor: "#ff4d4d",
    color: "white",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
  BookContainer: {
    display: "flex",
    justifyContent: "center",
    marginTop: "20px",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Cart;
