import React, { useEffect, useState } from 'react';

const UserProductViews = () => {
    const [medicines, setMedicines] = useState([]); // State to hold medicines

    const fetchMedicines = async () => {
        try {
            const response = await fetch('http://localhost:8000/medical_store_app/api/medicine/');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setMedicines(data); // Store fetched data in state
            console.log('Fetched Medicines:', data); // Debugging line
        } catch (error) {
            console.error('Error fetching medicines:', error);
        }
    };

    useEffect(() => {
        fetchMedicines(); // Fetch medicines on component mount
    }, []);

    // Handle Buy Now (can be used for checkout or other logic)
    const handleBuyNow = (medicine) => {
        console.log('Buying now:', medicine);
        // Add the functionality for buying the medicine directly here
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1 style={{ textAlign: 'center' }}>Available Medicines</h1>
            {medicines.length === 0 ? (
                <p style={{ textAlign: 'center' }}>No medicines found.</p>
            ) : (
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
                    {medicines.map((medicine) => (
                        <div
                            key={medicine.id}
                            style={{
                                border: '1px solid #ddd',
                                borderRadius: '8px',
                                padding: '20px',
                                width: '200px',
                                textAlign: 'center',
                                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                                marginBottom: '20px',
                            }}
                        >
                            <h3 style={{ marginBottom: '10px' }}>{medicine.name}</h3>
                            <p style={{ marginBottom: '10px' }}>{medicine.description}</p>
                            <p style={{ marginBottom: '10px' }}>Price: ${medicine.price}</p>
                            <div>
                                <button
                                    onClick={() => handleBuyNow(medicine)}
                                    style={{
                                        backgroundColor: '#007bff',
                                        color: 'white',
                                        padding: '10px 15px',
                                        border: 'none',
                                        borderRadius: '4px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Buy Now
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default UserProductViews;
