import React, { useState } from "react";
import Tablet from "../assets/image/Tablet.jpeg";
import Syrup from "../assets/image/syrup.webp";
import Capsule from "../assets/image/Capsule.jpeg";

const UserProductViews = () => {
  const products = [
    { id: 1, name: "Tablet", image: Tablet, price: 100 },
    { id: 2, name: "Capsule", image: Capsule, price: 150 },
    { id: 3, name: "Syrup", image: Syrup, price: 120 },
  ];

  const [cart, setCart] = useState([]); // Cart state
  const [searchTerm, setSearchTerm] = useState(""); // Search term state
  const [paymentMode, setPaymentMode] = useState(""); // Payment mode state
  const [formError, setFormError] = useState(""); // Error state for payment selection
  const [showReceipt, setShowReceipt] = useState(false); // Receipt display state

  // Handle product search
  const onSearchChange = (e) => setSearchTerm(e.target.value.toLowerCase());

  // Filter products based on search
  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm)
  );

  // Add product to the cart
  const addToCart = (product) => {
    setCart((prevCart) => {
      const productExists = prevCart.find((item) => item.id === product.id);
      if (productExists) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  // Remove product from the cart
  const removeFromCart = (productId) => setCart((prevCart) => prevCart.filter((item) => item.id !== productId));

  // Handle payment mode selection
  const handlePaymentChange = (e) => {
    setPaymentMode(e.target.value);
    setFormError(""); // Clear any previous errors
  };

  // Handle checkout process
  const handleCheckout = () => {
    if (!paymentMode) {
      setFormError("Please select a payment mode.");
      return;
    }

    // Clear cart and show the receipt
    setShowReceipt(true);
  };

  // Calculate total price of cart items
  const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div style={styles.pageContainer}>
      {/* Search Bar */}
      <div style={styles.searchBar}>
        <input
          type="text"
          placeholder="Search for products..."
          value={searchTerm}
          onChange={onSearchChange}
          style={styles.searchInput}
        />
      </div>

      {/* Product Section */}
      <section style={styles.productSection}>
        <h3>Available Products</h3>
        <div style={styles.productList}>
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div key={product.id} style={styles.productItem}>
                <img src={product.image} alt={product.name} style={styles.productImage} />
                <p>{product.name} - ${product.price}</p>
                <button
                  onClick={() => addToCart(product)}
                  style={styles.addButton}
                >
                  Add to Cart
                </button>
              </div>
            ))
          ) : (
            <p>No products found</p>
          )}
        </div>
      </section>

      {/* Cart Section */}
      <section style={styles.cartSection}>
        <h3>Your Cart</h3>
        {cart.length > 0 ? (
          <>
            <ul style={styles.cartList}>
              {cart.map((item) => (
                <li key={item.id} style={styles.cartItem}>
                  {item.name} (x{item.quantity}) - ${item.price * item.quantity}
                  <button
                    onClick={() => removeFromCart(item.id)}
                    style={styles.removeButton}
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>

            <h4>Total: ${totalPrice}</h4>

            {/* Payment Mode */}
            <div style={styles.paymentMode}>
              <h4>Select Payment Mode:</h4>
              <select value={paymentMode} onChange={handlePaymentChange} style={styles.paymentSelect}>
                <option value="">-- Select Payment Mode --</option>
                <option value="Credit Card">Credit Card</option>
                <option value="Debit Card">Debit Card</option>
                <option value="Cash on Delivery">Cash on Delivery</option>
                <option value="UPI">UPI</option>
              </select>
              {formError && <p style={styles.error}>{formError}</p>}
            </div>

            {/* Checkout Button */}
            <button onClick={handleCheckout} style={styles.checkoutButton}>
              Checkout
            </button>
          </>
        ) : (
          <p>Your cart is empty</p>
        )}
      </section>

      {/* Payment Receipt */}
      {showReceipt && (
        <section style={styles.receiptSection}>
          <h3>Payment Receipt</h3>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {item.name} (x{item.quantity}) - ${item.price * item.quantity}
              </li>
            ))}
          </ul>
          <p>Total Paid: ${totalPrice}</p>
          <p>Payment Method: {paymentMode}</p>
          <button
            onClick={() => {
              setCart([]); // Clear cart
              setPaymentMode(""); // Clear payment mode
              setShowReceipt(false); // Hide receipt
            }}
            style={styles.resetButton}
          >
            Done
          </button>
        </section>
      )}
    </div>
  );
};

// Inline CSS styles
const styles = {
  pageContainer: {
    fontFamily: "Arial, sans-serif",
    padding: "20px",
  },
  searchBar: {
    marginBottom: "20px",
    textAlign: "center",
  },
  searchInput: {
    padding: "10px",
    fontSize: "16px",
    width: "300px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  productSection: {
    textAlign: "center",
  },
  productList: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    flexWrap: "wrap",
  },
  productItem: {
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "10px",
    width: "150px",
    textAlign: "center",
  },
  productImage: {
    width: "100px",
    height: "100px",
    objectFit: "cover",
  },
  addButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "8px 16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  cartSection: {
    marginTop: "30px",
  },
  cartList: {
    listStyle: "none",
    paddingLeft: "0",
  },
  cartItem: {
    marginBottom: "10px",
  },
  removeButton: {
    backgroundColor: "#f44336",
    color: "white",
    padding: "6px 12px",
    border: "none",
    borderRadius: "5px",
    marginLeft: "10px",
    cursor: "pointer",
  },
  paymentMode: {
    margin: "20px 0",
  },
  paymentSelect: {
    padding: "10px",
    fontSize: "16px",
    width: "200px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  checkoutButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  error: {
    color: "red",
  },
  receiptSection: {
    marginTop: "30px",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
  },
  resetButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default UserProductViews;
