import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../axios";

export default function AddEditMedicine() {
  const user_id = sessionStorage.getItem("user_id");
  
  // Initialize the form values
  const [formValues, setFormValues] = useState({
    medicine_category: "",
    name: "",
    description: "",
    price: "",
    stock: "",
  });

  const [medicineCategories, setMedicineCategories] = useState([]);

  useEffect(() => {
    getMedicineCategories();
  }, []);

  // Fetch the medicine categories from the backend
  const getMedicineCategories = async () => {
    try {
      const response = await axiosInstance.get(apiUrl + "/medicine/categories");
      if (response.status === 200 && Array.isArray(response.data)) {
        setMedicineCategories(response.data);
        console.log("Fetched Categories:", response.data); // Debugging log
      } else {
        console.warn("Unexpected API response:", response);
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        generateRefreshToken(error);
      } else {
        console.error("Error fetching categories:", error);
      }
    }
  };

  // Handle form field changes
  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const submit = (e) => {
    e.preventDefault();
    addMedicine();
  };

  // Function to send POST request to the backend to add medicine
  const addMedicine = async () => {
    // Include user_id in the form values
    const medicineData = { ...formValues, user: user_id };
    console.log("Submitting Medicine Data:", medicineData);

    try {
      const response = await axiosInstance.post(apiUrl + "/medicine/", medicineData, {
        headers: { "Content-Type": "application/json" },
      });
      
      if (response.status === 200 || response.status === 201) {
        alert("Medicine added successfully!");
        // Reload or reset form after success
        setFormValues({
          medicine_category: "",
          name: "",
          description: "",
          price: "",
          stock: "",
        });
        window.location.reload(false);
      }
    } catch (error) {
      console.error("Error adding medicine:", error);
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>Add Medicines</h1>
      <form onSubmit={submit} method="post">
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="medicine_category" style={{ display: "block", marginBottom: "5px" }}>
            Medicine Category *
          </label>
          <select
            name="medicine_category"
            id="medicine_category"
            value={formValues.medicine_category}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
          >
            <option value="">Select</option>
            {medicineCategories.length > 0 ? (
              medicineCategories.map((category, i) => (
                <option value={category.id} key={i}>
                  {category.category_name || category.name} {/* Adjust according to API */}
                </option>
              ))
            ) : (
              <option value="">No categories available</option>
            )}
          </select>
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="name" style={{ display: "block", marginBottom: "5px" }}>
            Medicine Name *
          </label>
          <input
            type="text"
            name="name"
            id="name"
            value={formValues.name}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
            required
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="description" style={{ display: "block", marginBottom: "5px" }}>
            Description
          </label>
          <textarea
            name="description"
            id="description"
            value={formValues.description}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="price" style={{ display: "block", marginBottom: "5px" }}>
            Price *
          </label>
          <input
            type="number"
            name="price"
            id="price"
            value={formValues.price}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
            required
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="stock" style={{ display: "block", marginBottom: "5px" }}>
            Stock Quantity *
          </label>
          <input
            type="number"
            name="stock"
            id="stock"
            value={formValues.stock}
            onChange={onChangeHandler}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
            required
          />
        </div>

        <div>
          <button
            type="submit"
            style={{
              padding: "10px 20px",
              backgroundColor: "#007bff",
              color: "white",
              borderRadius: "5px",
              border: "none",
            }}
          >
            Add Medicine
          </button>
        </div>
      </form>
    </div>
  );
}
