import React, { useEffect, useState } from "react";
import axios from "axios";

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  cardContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "20px",
  },
  card: {
    backgroundColor: "#fff",
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "20px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  cardTitle: {
    marginBottom: "10px",
    fontSize: "1.2rem",
    fontWeight: "bold",
  },
  cardText: {
    margin: "5px 0",
  },
  buttonGroup: {
    marginTop: "10px",
    display: "flex",
    justifyContent: "space-between",
  },
  button: {
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
    flex: 1,
    margin: "0 5px",
  },
  confirmButton: {
    backgroundColor: "#4CAF50",
  },
  rejectButton: {
    backgroundColor: "#f44336",
  },
};

const MedicineBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:8000/medical_store_app/admin/bookings/");
        console.log(response.data);
        setBookings(response.data);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  const updateStatus = async (bookingId, status) => {
    try {
      await axios.post(`http://localhost:8000/medical_store_app/admin/bookings/${bookingId}/update/`, {
        status: status,
      });
      alert("Booking status updated!");
      setBookings(
        bookings.map((booking) =>
          booking.id === bookingId ? { ...booking, status } : booking
        )
      );
    } catch (error) {
      console.error("Error updating booking status:", error);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Admin - Manage Bookings</h1>
      {bookings.length === 0 ? (
        <p style={{ textAlign: "center", fontSize: "18px" }}>No bookings found</p>
      ) : (
        <div style={styles.cardContainer}>
          {bookings.map((booking) => (
            <div key={booking.id} style={styles.card}>
              <h3 style={styles.cardTitle}>{booking.customer}</h3>
              <p style={styles.cardText}><strong>Medicine:</strong> {booking.product}</p>
              <p style={styles.cardText}><strong>Quantity:</strong> {booking.quantity}</p>
              <p style={styles.cardText}>
                <strong>Status:</strong> {booking.status ? booking.status : "No status"}
              </p>
              <div style={styles.buttonGroup}>
                <button
                  onClick={() => updateStatus(booking.id, "confirmed")}
                  style={{ ...styles.button, ...styles.confirmButton }}
                >
                  Confirm
                </button>
                <button
                  onClick={() => updateStatus(booking.id, "rejected")}
                  style={{ ...styles.button, ...styles.rejectButton }}
                >
                  Reject
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MedicineBookings;
