import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../axios";

export default function ListMedicine() {
  const [medicines, setMedicines] = useState({});
  const [isEdit, setIsEdit] = useState(false);
  const [formValues, setFormValues] = useState({
    name: "",
    brand: "",
    price: "",
    quantity: "",
    description: "",
    category: "", // Added category to form values
  });

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const submit = (e) => {
    e.preventDefault();
    editMedicine();
  }

  const editMedicine = async () => {
    setFormValues({ ...formValues, ["user"]: sessionStorage?.getItem('user_id') });

    await axiosInstance
      .put(apiUrl + "/medicine/" + formValues?.id, formValues, {
        headers: { "Content-Type": "multipart/form-data" },
      })
      .then((response) => {
        if (response.status === 200) {
          alert(response.data);
          window.location.reload(false);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetch('/medicine/')
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // You should set the medicines state with the fetched data
        setMedicines(data);
      });
  }, []);

  const setEditRecord = (record) => {
    setIsEdit(true);
    setFormValues({ ...record });
  };

  const deleteMedicine = async (id) => {
    await axiosInstance
      .delete(apiUrl + `/medicine/${id}`)
      .then((response) => {
        if (response.status === 200) {
          alert(response.data);
          getMedicines();
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getMedicines = async () => {
    await axiosInstance
      .get(apiUrl + "/medicine/get/" + sessionStorage.getItem("user_id"))
      .then((response) => {
        if (response.status === 200) {
          setMedicines(response.data);
        }
      })
      .catch((error) => {
        if (error.response.status === 401) {
          generateRefreshToken(error);
        }
      });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h3 style={{ textAlign: "center", marginBottom: "20px" }}>List of Medicines</h3>
      <div style={{ marginTop: "30px" }}>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead style={{ backgroundColor: "#f8f9fa", textAlign: "left" }}>
            <tr>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>SLNo</th>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Medicine Name</th>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Brand</th>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Price</th>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Quantity</th>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Description</th>
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Category</th> {/* Added category column */}
              <th style={{ padding: "10px", border: "1px solid #dee2e6" }}>Action</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(medicines).map((key, i) => {
              return (
                <tr key={`medicine_rows${i}`} style={{ borderBottom: "1px solid #dee2e6" }}>
                  <td style={{ padding: "10px" }}>{i + 1}</td>
                  <td style={{ padding: "10px" }}>{medicines[key]["name"]}</td>
                  <td style={{ padding: "10px" }}>{medicines[key]["brand"]}</td>
                  <td style={{ padding: "10px" }}>{medicines[key]["price"]}</td>
                  <td style={{ padding: "10px" }}>{medicines[key]["quantity"]}</td>
                  <td style={{ padding: "10px" }}>{medicines[key]["description"]}</td>
                  <td style={{ padding: "10px" }}>{medicines[key]["category"]}</td> {/* Added category cell */}
                  <td style={{ padding: "10px" }}>
                    <a
                      href="#"
                      onClick={() => setEditRecord(medicines[key])}
                      style={{ padding: "5px", color: "blue", cursor: "pointer", textDecoration: "underline" }}
                    >
                      Edit
                    </a>
                    <a
                      href="#"
                      onClick={() => deleteMedicine(medicines[key]["id"])}
                      style={{ padding: "5px", color: "red", cursor: "pointer", textDecoration: "underline" }}
                    >
                      Delete
                    </a>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      {isEdit && (
        <div style={{ marginTop: "20px" }}>
          <form onSubmit={submit} method="post">
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <div style={{ width: "48%" }}>
                <label>Medicine Name</label>
                <input
                  type="text"
                  name="name"
                  onChange={onChangeHandler}
                  value={formValues?.name || ""}
                  style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
                />
              </div>
              <div style={{ width: "48%" }}>
                <label>Brand</label>
                <input
                  type="text"
                  name="brand"
                  onChange={onChangeHandler}
                  value={formValues?.brand || ""}
                  style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
                />
              </div>
            </div>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <div style={{ width: "48%" }}>
                <label>Price</label>
                <input
                  type="text"
                  name="price"
                  onChange={onChangeHandler}
                  value={formValues?.price || ""}
                  style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
                />
              </div>
              <div style={{ width: "48%" }}>
                <label>Quantity</label>
                <input
                  type="number"
                  name="quantity"
                  onChange={onChangeHandler}
                  value={formValues?.quantity || ""}
                  style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
                />
              </div>
            </div>
            <div style={{ width: "100%" }}>
              <label>Description</label>
              <textarea
                name="description"
                onChange={onChangeHandler}
                value={formValues?.description || ""}
                style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
              />
            </div>
            <div style={{ width: "48%" }}>
              <label>Category</label>
              <input
                type="text"
                name="category"
                onChange={onChangeHandler}
                value={formValues?.category || ""}
                style={{ width: "100%", padding: "10px", marginBottom: "10px" }}
              />
            </div>
            <button type="submit" style={{ padding: "10px", backgroundColor: "blue", color: "white" }}>
              Update Medicine
            </button>
          </form>
        </div>
      )}
    </div>
  );
}
