import React, { useState } from 'react';

const Dashboard = () => {
  const totalCustomers = 1;
  const totalBookings = 0;
  const totalProducts = 13;

  // Separate states for each dropdown
  const [showProductDropdown, setShowProductDropdown] = useState(false);
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);

  // Toggle for Product dropdown
  const toggleProductDropdown = () => {
    setShowProductDropdown(!showProductDropdown);
    setShowCategoryDropdown(false); // Close Category dropdown if Product is opened
  };

  // Toggle for Category dropdown
  const toggleCategoryDropdown = () => {
    setShowCategoryDropdown(!showCategoryDropdown);
    setShowProductDropdown(false); // Close Product dropdown if Category is opened
  };

  const styles = {
    dashboard: {
      fontFamily: "'Arial', sans-serif",
      textAlign: 'center',
      padding: '20px',
      backgroundColor: '#f8f9fa',
      height: '100vh'
    },
    header: {
      backgroundColor: '#000',
      padding: '15px',
      color: 'white',
      textAlign: 'left',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    nav: {
      display: 'flex',
      justifyContent: 'flex-end',
      marginRight: '20px',
      position: 'relative',
      alignItems: 'center'
    },
    navLink: {
      color: 'white',
      margin: '0 15px',
      textDecoration: 'none',
      position: 'relative',
      cursor: 'pointer'
    },
    dropdownMenu: {
      position: 'absolute',
      top: '100%',
      backgroundColor: '#333',
      borderRadius: '4px',
      padding: '10px',
      zIndex: 1,
      display: 'none'
    },
    dropdownMenuOpen: {
      display: 'block'
    },
    dropdownItem: {
      color: 'white',
      padding: '10px 15px',
      textDecoration: 'none',
      display: 'block',
      backgroundColor: '#444',
      margin: '5px 0',
      borderRadius: '4px'
    }
  };

  return (
    <div style={styles.dashboard}>
      <header style={styles.header}>
        <h1>Online Medical Shop Admin</h1>
        <nav style={styles.nav}>
          <a href="Home" style={styles.navLink}>Home</a>
          <a href="#" style={styles.navLink}>View User</a>
          <a href="#" style={styles.navLink}>View Booking</a>
          <a href="#" style={styles.navLink}>View Feedback</a>
          
          {/* Product Dropdown */}
          <div style={styles.navLink} onClick={toggleProductDropdown}>
            Products ▼
            <div style={{ ...styles.dropdownMenu, ...(showProductDropdown && styles.dropdownMenuOpen) }}>
              <a href="#" style={styles.dropdownItem}>Add Product</a>
              <a href="#" style={styles.dropdownItem}>View Product</a>
            </div>
          </div>

          {/* Category Dropdown */}
          <div style={styles.navLink} onClick={toggleCategoryDropdown}>
            Category ▼
            <div style={{ ...styles.dropdownMenu, ...(showCategoryDropdown && styles.dropdownMenuOpen) }}>
              <a href="#" style={styles.dropdownItem}>Add Category</a>
              <a href="#" style={styles.dropdownItem}>View Category</a>
            </div>
          </div>

          <a href="#" style={styles.navLink}>Logout</a>
        </nav>
      </header>
      
    </div>
  );
};

export default Dashboard;
