import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; 
import { useSelector } from "react-redux";
import { Spinner, Container, Row, Col, Card, Image, Button, Link } from "react-bootstrap";
import Message from "../components/Message";
import UserAddressComponent from "../components/UserAddressComponent";
import ChargeCardComponent from "../components/ChargeCardComponent";
import CreateCardComponent from "../components/CreateCardComponent";

const Checkout = () => {
  const [paymentMethod, setPaymentMethod] = useState("COD"); 
  const [address, setAddress] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [loading, setLoading] = useState(false); 
  const navigate = useNavigate(); 

  // Redux selectors
  const productDetails = useSelector((state) => state.productDetailsReducer);
  const { product, loading: productLoading, error: productError } = productDetails;

  const cardCreation = useSelector((state) => state.createCardReducer);
  const { success: cardCreationSuccess, loading: cardCreationLoading, error: cardCreationError } = cardCreation;

  const userLogin = useSelector((state) => state.userLoginReducer);
  const { userInfo } = userLogin;

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (address.trim() === "") {
      alert("Please enter a valid address.");
      return;
    }

    setLoading(true); 

    try {
      const orderData = {
        payment_method: paymentMethod,
        address: address,
      };

      const response = await axios.post("http://localhost:8000/medical_store_app/order/", orderData);
      
      console.log("Order submitted successfully:", response.data);
      
      setIsSubmitted(true);

      setTimeout(() => {
        navigate("/order-confirmation");
      }, 2000);
      
    } catch (error) {
      console.error("Error submitting order:", error);
      alert("There was an error submitting your order. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      {cardCreationError && <Message variant='danger'>{cardCreationError}</Message>}
      {productLoading && (
        <span style={{ display: "flex" }}>
          <h5>Getting Checkout Info</h5>
          <span className="ml-2">
            <Spinner animation="border" />
          </span>
        </span>
      )}
      {cardCreationLoading && !loading && (
        <span style={{ display: "flex" }}>
          <h5>Checking your card</h5>
          <span className="ml-2">
            <Spinner animation="border" />
          </span>
        </span>
      )}
      {productError ? (
        <Message variant='danger'>{productError}</Message>
      ) : (
        <Container>
          <Row>
            <Col xs={6}>
              <h3>Checkout Summary</h3>
              <Card className="mb-4">
                <Card.Body>
                  <Container>
                    <Row>
                      <Col>
                        <Image src={product?.image} alt="image" height="180" />
                      </Col>
                      <Col>
                        <h5 className="card-title text-capitalize">{product?.name}</h5>
                        <span className="card-text text-success">₹ {product?.price}</span>
                      </Col>
                    </Row>
                  </Container>
                </Card.Body>
              </Card>

              <div style={{ display: "flex" }}>
                <h3>Billing Address</h3>
                <Link className="ml-2 mt-2" to="/all-addresses/">
                  Edit/Add Address
                </Link>
              </div>
              <UserAddressComponent handleAddressId={(id) => setAddress(id)} />
            </Col>
            <Col xs={6}>
              <h3>Payments Section</h3>
              {cardCreationSuccess ? (
                <ChargeCardComponent addressSelected={address && address.trim()} />
              ) : (
                <CreateCardComponent />
              )}
            </Col>
          </Row>
          {!isSubmitted && (
            <form onSubmit={handleSubmit}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Shipping Address:</label>
                <textarea
                  style={styles.input}
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  placeholder="Enter your delivery address"
                />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Payment Method:</label>
                <div style={styles.radioContainer}>
                  <label style={styles.radioLabel}>
                    <input
                      type="radio"
                      value="COD"
                      checked={paymentMethod === "COD"}
                      onChange={() => setPaymentMethod("COD")}
                    />
                    Cash on Delivery (COD)
                  </label>
                </div>
              </div>

              <button type="submit" style={styles.submitButton}>
                Proceed to Payment
              </button>
            </form>
          )}
        </Container>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "30px",
    fontFamily: "Arial, sans-serif",
    maxWidth: "600px",
    margin: "0 auto",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
    color: "#333",
  },
  formGroup: {
    marginBottom: "15px",
  },
  label: {
    fontSize: "1.1rem",
    fontWeight: "bold",
    marginBottom: "5px",
    display: "block",
  },
  input: {
    width: "100%",
    padding: "10px",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    boxSizing: "border-box",
    height: "100px",
  },
  radioContainer: {
    display: "flex",
    flexDirection: "column",
  },
  radioLabel: {
    fontSize: "1rem",
    marginBottom: "10px",
  },
  submitButton: {
    width: "100%",
    padding: "12px",
    fontSize: "1.2rem",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
};

export default Checkout;
