import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { apiUrl } from '../axios'; // Ensure this is correctly defined.

export default function ChangePassword() {
  const [formValues, setFormValues] = useState({
    current_password: '',
    change_password: '',
    confirm_password: ''
  });

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
    console.log(formValues); // Debugging log
  };

  const callAPI = () => {
    const user_id = sessionStorage.getItem('user_id');
    
    // Include the user_id when making the request
    const data = { ...formValues, user_id };
    
    axios.post(apiUrl + "/medical_store_app/change_password", data)
      .then((response) => {
        if (response.status === 200) {
          alert(response.data); // Assuming the response has a message.
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    // If you need to fetch data on component mount, handle it here.
  }, []);

  // Define inline styles
  const formGroupStyle = {
    marginBottom: '15px'
  };

  const labelStyle = {
    fontWeight: 'bold',
    marginBottom: '5px'
  };

  const inputStyle = {
    width: '100%',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc'
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  };

  return (
    <div>
      <div style={formGroupStyle}>
        <label htmlFor="current_password" style={labelStyle}>
          Current Password
        </label>
        <input
          type="password"
          name="current_password"
          style={inputStyle}
          id="current_password"
          placeholder="Current Password"
          onChange={onChangeHandler}
        />
      </div>
      
      <div style={formGroupStyle}>
        <label htmlFor="change_password" style={labelStyle}>
          New Password
        </label>
        <input
          type="password"
          name="change_password"
          style={inputStyle}
          id="change_password"
          placeholder="New Password"
          onChange={onChangeHandler}
        />
      </div>

      <div style={formGroupStyle}>
        <label htmlFor="confirm_password" style={labelStyle}>
          Confirm New Password
        </label>
        <input
          type="password"
          name="confirm_password"
          style={inputStyle}
          id="confirm_password"
          placeholder="Confirm New Password"
          onChange={onChangeHandler}
        />
      </div>

      <button type="button" onClick={callAPI} style={buttonStyle}>
        Submit
      </button>
    </div>
  );
}
