import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../../axios";
import axios from "axios";
import jwt_decode from "jwt-decode"; // Added JWT decode to check token expiration

export default function MedicineBookings() {
  const [medicineBookings, setMedicineBookings] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getMedicineBookings();
  }, []);

  // Function to update booking status
  const updateMedicineBookingStatus = async (bookingId, statusType) => {
    try {
      const response = await axiosInstance.put(
        `${apiUrl}/medical_store_app/medicine/book/${bookingId}/${statusType}`
      );
      if (response.status === 200) {
        alert(response.data.message);
        window.location.reload(false); // Reload page after update
      }
    } catch (error) {
      console.error("Error updating booking status:", error);
      if (error.response?.status === 401) {
        console.log("Unauthorized: Refreshing token...");
        generateRefreshToken(error);
      }
    }
  };

  // Function to fetch medicine bookings
  const getMedicineBookings = async () => {
    const token = localStorage.getItem("auth_token");

    if (!token) {
      console.log("No token found.");
      return;
    }

    // Check if the token is expired
    try {
      const decodedToken = jwt_decode(token);
      const currentTime = Date.now() / 1000; // in seconds
      if (decodedToken.exp < currentTime) {
        console.log("Token expired");
        generateRefreshToken(); // Handle token refresh
        return; // Prevent making the API request with an expired token
      }
    } catch (error) {
      console.error("Error decoding token:", error);
      return;
    }

    try {
      const response = await axios.get(
        `${apiUrl}/medical_store_app/medicine/owner/booking/9/`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log(response.data);
      setMedicineBookings(response.data);
    } catch (error) {
      if (error.response?.status === 401) {
        console.log("Unauthorized: Token expired or invalid.");
        generateRefreshToken(error); // Handle token refresh
      } else {
        console.error("Network or other error:", error);
      }
    }
  };

  // Function to handle token refresh
  const generateRefreshToken = async () => {
    const refreshToken = localStorage.getItem("refresh_token");
    if (!refreshToken) {
      console.log("No refresh token found.");
      // Redirect to login or show an error message
      return;
    }

    try {
      const response = await axios.post(
        `${apiUrl}/refresh_token`, // Replace with your refresh token endpoint
        { refresh_token: refreshToken }
      );
      const newToken = response.data.token;
      localStorage.setItem("auth_token", newToken); // Save new access token
      console.log("Token refreshed successfully.");
      getMedicineBookings(); // Retry the original API request after refreshing the token
    } catch (err) {
      console.error("Error refreshing token:", err);
      // Handle token refresh error, maybe log out or redirect to login page
    }
  };

  // Styling objects
  const containerStyle = {
    margin: "50px",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
  };

  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    backgroundColor: "#f9f9f9",
    marginTop: "20px",
  };

  const thStyle = {
    backgroundColor: "#2a2a2a",
    color: "white",
    padding: "12px",
    textAlign: "center",
    fontSize: "1rem",
  };

  const tdStyle = {
    padding: "10px",
    border: "1px solid #ddd",
    textAlign: "center",
    fontSize: "0.9rem",
    backgroundColor: "#fff",
  };

  const buttonStyle = {
    padding: "8px 16px",
    fontSize: "0.9rem",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    color: "#fff",
  };

  return (
    <div>
      <div style={containerStyle}>
        <div style={{ marginTop: "30px" }}>
          {loading ? (
            <div>Loading...</div>
          ) : (
            <table style={tableStyle}>
              <thead>
                <h2>View Bookings</h2>
                <tr>
                  <th style={thStyle}>SLNo</th>
                  <th style={thStyle}>Medicine</th>
                  <th style={thStyle}>Booking Status</th>
                  <th style={thStyle}>Booked By</th>
                  <th style={thStyle}>From Time</th>
                  <th style={thStyle}>To Time</th>
                  <th style={thStyle}>Created At</th>
                  <th style={thStyle}>Action</th>
                </tr>
              </thead>
              <tbody>
                {Object.keys(medicineBookings).map((key, i) => (
                  <tr key={`medicine_rows${i}`}>
                    <td style={tdStyle}>{i + 1}</td>
                    <td style={tdStyle}>{medicineBookings[key]["medicine_name"]}</td>
                    <td style={tdStyle}>{medicineBookings[key]["booking_status"]}</td>
                    <td style={tdStyle}>{medicineBookings[key]["booking_by_name"]}</td>
                    <td style={tdStyle}>
                      {new Date(medicineBookings[key]["from_time"]).toLocaleString()}
                    </td>
                    <td style={tdStyle}>
                      {new Date(medicineBookings[key]["to_time"]).toLocaleString()}
                    </td>
                    <td style={tdStyle}>
                      {new Date(medicineBookings[key]["created_at"]).toLocaleString()}
                    </td>

                    <td style={tdStyle}>
                      {medicineBookings[key]["booking_status"] === "Pending" ? (
                        <>
                          <a
                            href="#"
                            onClick={() =>
                              updateMedicineBookingStatus(medicineBookings[key]["id"], "confirm")
                            }
                            className="btn btn-success"
                            style={buttonStyle}
                          >
                            Approve
                          </a>
                          <a
                            href="#"
                            onClick={() =>
                              updateMedicineBookingStatus(medicineBookings[key]["id"], "reject")
                            }
                            className="btn btn-danger"
                            style={buttonStyle}
                          >
                            Reject
                          </a>
                        </>
                      ) : medicineBookings[key]["booking_status"] === "Confirmed" ? (
                        <>
                          <a
                            href="#"
                            onClick={() =>
                              updateMedicineBookingStatus(medicineBookings[key]["id"], "complete")
                            }
                            className="btn btn-secondary"
                            style={buttonStyle}
                          >
                            Complete
                          </a>
                        </>
                      ) : medicineBookings[key]["booking_status"] === "Payment Complete" ? (
                        <span>Payment Complete</span>
                      ) : (
                        medicineBookings[key]["booking_status"]
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}
