import React, { useState, useEffect } from 'react';

// Sample data (you can replace this with data fetched from an API or backend)
const sampleProducts = [
  {
    id: 1,
    name: 'Paracetamol',
    price: '5.00',
    description: 'Used for pain relief and fever reduction.',
    inStock: true,
  },
  {
    id: 2,
    name: 'Ibuprofen',
    price: '10.00',
    description: 'Used to reduce inflammation and treat pain.',
    inStock: false,
  },
  {
    id: 3,
    name: 'Aspirin',
    price: '8.50',
    description: 'Used as a pain reliever and to reduce fever or inflammation.',
    inStock: true,
  },
];

function Product() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  // Fetch product data (if needed)
  useEffect(() => {
    // You can replace this with an API call to fetch products
    setProducts(sampleProducts);
  }, []);

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
    alert(`${product.name} has been added to your cart!`);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Product List</h1>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {products.map((product) => (
          <li key={product.id} style={{ border: '1px solid #ccc', borderRadius: '5px', margin: '10px 0', padding: '15px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
            <h2 style={{ margin: '0', color: '#007BFF' }}>{product.name}</h2>
            <p style={{ margin: '5px 0' }}>{product.description}</p>
            <p style={{ fontWeight: 'bold' }}>
              Price: <span style={{ color: '#28A745' }}>${product.price}</span>
            </p>
            <p style={{ fontStyle: 'italic' }}>{product.inStock ? 'In Stock' : 'Out of Stock'}</p>
            {/* Add to Cart Button */}
            <button
              style={{
                backgroundColor: product.inStock ? '#28a745' : '#ccc',
                color: 'white',
                padding: '10px',
                border: 'none',
                cursor: product.inStock ? 'pointer' : 'not-allowed',
                borderRadius: '4px',
              }}
              onClick={() => product.inStock && addToCart(product)}
              disabled={!product.inStock}
            >
              Add to Cart
            </button>
          </li>
        ))}
      </ul>
      {/* Optional: Display Cart Items */}
      <div style={{ marginTop: '20px' }}>
        <h2>Your Cart</h2>
        {cart.length === 0 ? (
          <p>No items in cart.</p>
        ) : (
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {cart.map((item, index) => (
              <li key={index}>
                {item.name} - ${item.price}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Product; // Export the component
