import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios";
import { jwtDecode } from "jwt-decode";  // Correct named import

export default function Login() {
  const navigate = useNavigate();
  const [formvalues, setformvalues] = useState({});
  const [formError, setFromError] = useState("");
  const onChangeHandler = (e) => {
    setformvalues({ ...formvalues, [e.target.name]: e.target.value });
  };
  const onSubmit = (e) => {
    e.preventDefault();
    axios
      .post(apiUrl + "/api/token/", formvalues)
      .then((response) => {
        sessionStorage.setItem("access_token", response.data.access);
        sessionStorage.setItem("refresh_token", response.data.refresh);
        
        // Correct usage of jwtDecode
        var user = jwtDecode(response.data.access);
        var userId = user.user_id;
        var username = user.name;
        var user_type = user.user_type;

        axios.defaults.headers.common[
          "Authorization"
        ] = `Bearer ${response.data.access}`;
        
        sessionStorage.setItem("user_id", userId);
        sessionStorage.setItem("name", username);
        sessionStorage.setItem("user_type", user_type);

        if (user_type === "admin") navigate("/Admin/DashBoard");
        else if (user_type === "user") navigate("/User/DashBord");
        
      })
      .catch((error) => {
        if (error.response.status === 401) {
          setFromError(error.response.data.detail);
        }
      });
  };
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      backgroundColor: '#f4f4f4',
    },
    form: {
      background: '#fff',
      padding: '20px',
      borderRadius: '5px',
      boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
      width: '300px',
      textAlign: 'center',
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: 'bold',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      border: '1px solid #ccc',
      borderRadius: '4px',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#007BFF',
      color: '#fff',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontWeight: 'bold',
    },
    switchButton: {
      marginTop: '10px',
      padding: '10px',
      backgroundColor: '#6c757d',
      color: '#fff',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontWeight: 'bold',
    },
    error: {
      color: 'red',
      marginTop: '10px',
    },
  };

  return (
    <div style={styles.container}>
      <form style={styles.form} onSubmit={handleLogin}>
        <div>
          <label style={styles.label}>
            Username:
            <input
              type="text"
              name="username"
              value={credentials.username}
              onChange={handleInputChange}
              required
              style={styles.input}
            />
          </label>
        </div>
        <div>
          <label style={styles.label}>
            Password:
            <input
              type="password"
              name="password"
              value={credentials.password}
              onChange={handleInputChange}
              required
              style={styles.input}
            />
          </label>
        </div>
        <button type="submit" style={styles.button}>Login</button>
      </form>
      <button style={styles.switchButton} onClick={() => setIsAdmin(!isAdmin)}>
        Switch to {isAdmin ? 'User' : 'Admin'} Login
      </button>
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};
