import React, { useState, useEffect } from "react";

function PrescriptionCreateForm() {
  const [medicines, setMedicines] = useState([]);
  const [formData, setFormData] = useState({
    medicine_id: "",
    quantity: "",
    notes: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Fetch available medicines from the API
  useEffect(() => {
    fetch("/api/medicines/")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setMedicines(data);
        } else {
          console.error("Medicine data is not in expected format:", data);
          setMedicines([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching medicines:", error);
        setError("Failed to load medicines. Please try again later.");
      });
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };


  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccessMessage("");
    fetch("/api/prescriptions/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        setLoading(false);
        if (response.ok) {
          setSuccessMessage("Prescription created successfully!");
          setFormData({
            medicine_id: "",
            quantity: "",
            notes: "",
          });
        } else {
          return response.json().then((data) => {
            throw new Error(data.error || "Failed to create prescription.");
          });
        }
      })
      .catch((error) => {
        setLoading(false);
        setError(error.message || "An error occurred. Please try again.");
      });
  };

  return (
    <div>
      <h2>Create Prescription</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="medicine_id">Select Medicine:</label>
          <select
            id="medicine_id"
            name="medicine_id"
            value={formData.medicine_id}
            onChange={handleChange}
            required
          >
            <option value="">-- Select Medicine --</option>
            {medicines.map((medicine) => (
              <option key={medicine.id} value={medicine.id}>
                {medicine.name} - â‚¹{medicine.price}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleChange}
            required
            min="1"
          />
        </div>
        <div>
          <label htmlFor="notes">Notes (optional):</label>
          <textarea
            id="notes"
            name="notes"
            value={formData.notes}
            onChange={handleChange}
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Creating..." : "Create Prescription"}
        </button>
      </form>
    </div>
  );
}

export default PrescriptionCreateForm;
