import React, { useEffect, useState, useCallback, useMemo } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  // Memoize user_data
  const user_data = useMemo(() => ({ user_id: sessionStorage.getItem("user_id") }), []); // Memoize user_data

  // Memoize fetchCart with useCallback
  const fetchCart = useCallback(async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data
      );
      if (response.data.cart_items.length === 0) {
        console.log("Cart is empty.");
      } else {
        console.log("Cart items:", response.data.cart_items);
      }
      setCartItems(response.data.cart_items);
      setTotalPrice(response.data.total_price);

      // Optionally store cart in localStorage for persistence
      localStorage.setItem("cartItems", JSON.stringify(response.data.cart_items));
      localStorage.setItem("totalPrice", response.data.total_price);
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
  }, [user_data]); // Add user_data as a dependency

  // useEffect hook with fetchCart added as a dependency
  useEffect(() => {
    const userId = sessionStorage.getItem("user_id");
    if (userId) {
      fetchCart(); // Fetch the cart items only if user is logged in
    } else {
      // Check if there are saved cart items in localStorage
      const savedCartItems = JSON.parse(localStorage.getItem("cartItems"));
      const savedTotalPrice = localStorage.getItem("totalPrice");
      if (savedCartItems && savedTotalPrice) {
        setCartItems(savedCartItems);
        setTotalPrice(savedTotalPrice);
      } else {
        setCartItems([]); // Set cart to empty if no saved items
        setTotalPrice(0); // Reset total price
      }
    }
  }, [fetchCart]); // Re-run effect if fetchCart changes

  const handleRemoveFromCart = async (cartId) => {
    if (!cartId) {
      console.error("Cart ID is undefined!");
      return;
    }

    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem("user_id")}`
      );
      fetchCart(); // Refresh cart after removal
    } catch (error) {
      console.error("Error removing cart item:", error);
    }
  };

  const handleProceedToCheckout = () => {
    navigate("/payment", { state: { totalPrice } });
  };

  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  const handleLogout = () => {
    sessionStorage.removeItem("user_id"); // Clear user session data
    localStorage.removeItem("cartItems"); // Remove saved cart from localStorage
    localStorage.removeItem("totalPrice"); // Remove saved total price
    setCartItems([]); // Clear cart items
    setTotalPrice(0); // Reset total price
    navigate("/login"); // Redirect to login page
  };

  return (
    <div style={styles.container}>
      <button style={styles.continueShoppingButton} onClick={handleContinueShopping}>
        Continue Shopping
      </button>
      <button style={styles.logoutButton} onClick={handleLogout}>
        Logout
      </button>
      <h1 style={styles.heading}>Your Cart</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td>{item.medicine.name}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleRemoveFromCart(item.id)} // Pass the correct cart item id
                  style={styles.button}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: ${totalPrice}</h3>
      <div style={styles.paymentContainer}>
        <button style={styles.paymentButton} onClick={handleProceedToCheckout}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  continueShoppingButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  logoutButton: {
    backgroundColor: "#ff4d4d",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
    marginLeft: "10px",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  button: {
    backgroundColor: "#ff4d4d",
    color: "white",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
  paymentContainer: {
    textAlign: "right",
    marginTop: "20px",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Cart;
