import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../axios"; // Import the login function

export default function Login() {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({ username: "", password: "" });
  const [formError, setFormError] = useState("");
  const [loading, setLoading] = useState(false);

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const data = await login(formValues); // Call login with formValues
      console.log("Login successful:", data);
      // Handle successful login (e.g., store tokens, navigate)
      sessionStorage.setItem("access_token", data.token);
      sessionStorage.setItem("refresh_token", data.refresh);
      // Optionally decode token and store user info
      navigate("/admin/users"); // Redirect upon successful login
    } catch (error) {
      console.error("Login error:", error);
      setFormError("Login failed. Please check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)" }}>
      <div className="container mt-5" style={{ width: "450px", padding: "40px", backgroundColor: "#ffffff", borderRadius: "15px", boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)" }}>
        <form onSubmit={onSubmit}>
          <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" }}>Admin Login</h2>
          
          {/* Username */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="username" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Username:</label>
            <input
              required
              type="text"
              name="username"
              id="username"
              placeholder="Enter your username"
              className="form-control"
              onChange={onChangeHandler}
            />
          </div>

          {/* Password */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="password" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Password:</label>
            <input
              required
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              className="form-control"
              onChange={onChangeHandler}
            />
          </div>

          {/* Error message */}
          <p className="text-danger" style={{ fontSize: "14px", marginBottom: "20px" }}>{formError}</p>

          {/* Submit button */}
          <div className="text-center">
            <button className="btn btn-dark" style={{ width: "50%", backgroundColor: "#007bff", border: "none", borderRadius: "8px" }} disabled={loading}>
              {loading ? "Logging in..." : "Login"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
