import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../Admin/Navbar';
import axios from "axios";
import './UserProductViews.css'; // Assuming styles are moved to a separate CSS file

const BASE_URL = 'http://localhost:8000/medical_store_app'; // Centralize API URL

const UserProductViews = () => {
    const [medicines, setMedicines] = useState([]);
    const [cart, setCart] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMedicines = async () => {
            setLoading(true);
            setError(null);
            try {
                const response = await axios.get(`${BASE_URL}/api/medicine/`);
                setMedicines(response.data);
            } catch (err) {
                setError('Failed to fetch medicines. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchMedicines();
    }, []);

    const handleAddToCart = async (medicine) => {
        if (!sessionStorage.getItem('user_id')) {
            alert('Please log in to add items to your cart.');
            return;
        }

        if (cart.find((item) => item.id === medicine.id)) {
            alert('Item already in cart.');
            return;
        }

        try {
            await axios.post(`${BASE_URL}/cart/add/${medicine.id}/${sessionStorage.getItem('user_id')}`);
            setCart((prevCart) => [...prevCart, medicine]);
        } catch (err) {
            console.error('Error adding item to cart:', err);
        }
    };

    const handleBuyNow = async (medicine) => {
        try {
            await handleAddToCart(medicine);
            navigate('/cart');
        } catch (err) {
            console.error('Error handling Buy Now:', err);
        }
    };

    if (loading) {
        return <div className="loading">Loading medicines...</div>;
    }

    if (error) {
        return <div className="error">{error}</div>;
    }

    return (
        <div className="container">
            <Navbar cartCount={cart.length} />
            <h1 className="title">Available Medicines</h1>
            {medicines.length === 0 ? (
                <div className="no-medicines">
                    <p>No medicines found.</p>
                    <button onClick={() => navigate('/')} className="back-button">
                        Go Back
                    </button>
                </div>
            ) : (
                <div className="products-container">
                    {medicines.map((medicine) => (
                        <ProductCard
                            key={medicine.id}
                            medicine={medicine}
                            onAddToCart={handleAddToCart}
                            onBuyNow={handleBuyNow}
                        />
                    ))}
                </div>
            )}
        </div>
    );
};

const ProductCard = ({ medicine, onAddToCart, onBuyNow }) => (
    <div className="product-card">
        <h3 className="product-name">{medicine.name}</h3>
        <p className="product-description">{medicine.description}</p>
        <p className="product-price">Price: ${medicine.price}</p>
        <div className="button-group">
            <button onClick={() => onAddToCart(medicine)} className="add-to-cart-button">
                Add to Cart
            </button>
            <button onClick={() => onBuyNow(medicine)} className="buy-now-button">
                Buy Now
            </button>
        </div>
    </div>
);

export default UserProductViews;
