import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { axiosInstance } from "../axios"; // Import the axios instance

const Login = () => {
  const [formValues, setFormValues] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormValues({
      ...formValues,
      [e.target.name]: e.target.value,
    });
    setSuccess(null);
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axiosInstance.post("/api/token/", {
        username: formValues.username,
        password: formValues.password,
      });

      sessionStorage.setItem("access_token", response.data.access);
      sessionStorage.setItem("refresh_token", response.data.refresh);
      axiosInstance.defaults.headers["Authorization"] = "Bearer " + response.data.access;
      setSuccess("Login successful!");
      navigate("/dashboard");
    } catch (error) {
      setError("Invalid username or password");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)" }}>
      <div className="container mt-5" style={{ width: "450px", padding: "40px", backgroundColor: "#ffffff", borderRadius: "15px", boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)" }}>
        <form onSubmit={handleSubmit}>
          <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" }}>Admin Login</h2>
          
          {/* Username */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="username" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Username:</label>
            <input
              required
              type="text"
              name="username"
              id="username"
              placeholder="Enter your username"
              className="form-control"
              onChange={handleChange}
            />
          </div>

          {/* Password */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="password" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Password:</label>
            <input
              required
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              className="form-control"
              onChange={handleChange}
            />
          </div>

          {/* Error message */}
          {error && <p className="text-danger" style={{ fontSize: "14px", marginBottom: "20px" }}>{error}</p>}
          
          {/* Success message */}
          {success && <p className="text-success" style={{ fontSize: "14px", marginBottom: "20px" }}>{success}</p>}

          {/* Submit button */}
          <div className="text-center">
            <button className="btn btn-dark" style={{ width: "50%", backgroundColor: "#007bff", border: "none", borderRadius: "8px" }} disabled={loading}>
              {loading ? "Logging in..." : "Login"}
            </button>
          </div>
        </form>

        {/* Forgot Password Link */}
        <div className="text-center" style={{ marginTop: "20px" }}>
          <button onClick={() => navigate("/forgopassword")} style={{ background: "none", border: "none", color: "#007bff", cursor: "pointer" }}>
            Forgot Password?
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
