import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const PaymentForm = () => {
  const [amount, setAmount] = useState(0);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);

  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  // Ensure the amount is in rupees and not paise
  useEffect(() => {
    if (totalPrice > 0) {
      setAmount(totalPrice / 100); // Convert paise to rupees if needed
    } else {
      setError('Invalid total price');
    }
  }, [totalPrice]);

  const refreshToken = async () => {
    const refreshToken = localStorage.getItem('refresh_token');
    if (!refreshToken) {
      setError('No refresh token available');
      return null;
    }

    try {
      const response = await fetch('http://localhost:8000/api/token/refresh/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ refresh: refreshToken }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('auth_token', data.access); // Save the new access token
        return data.access;
      } else {
        setError('Failed to refresh token');
        return null;
      }
    } catch (err) {
      setError('Error refreshing token');
      return null;
    }
  };

  

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      {error && <div style={styles.error}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label style={styles.label}>Amount</label>
          <input
            type="number"
            value={amount}  // Display amount in rupees
            readOnly
            style={styles.input}
          />
        </div>
        <div>
          <button
            type="submit"
            style={processing ? { ...styles.button, ...styles.buttonDisabled } : styles.button}
            disabled={processing}
          >
            {processing ? 'Processing...' : 'Place Order'}
          </button>
        </div>
      </form>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
};

export default PaymentForm;
