import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { order, totalPrice, clearCart } = location.state;

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.onload = () => {
      const initializeRazorpay = () => {
        const options = {
          key: "rzp_test_a6hVvFHJbT9SAO",
          amount: totalPrice * 100,
          currency: "INR",
          name: "Your Company Name",
          description: "Payment for Order #" + order.id,
          order_id: order.id,
          handler: function (response) {
            alert("Payment successful");
            if (clearCart) {
              clearCart();
            }
            navigate("/booking", {
              state: { order, paymentDetails: response },
            });
          },
        };
        const razorpay = new window.Razorpay(options);
        razorpay.open();
      };
      initializeRazorpay();
    };
    script.onerror = () => {
      alert("Failed to load Razorpay script.");
    };
    document.body.appendChild(script);
  }, [order, totalPrice, navigate, clearCart]);

  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      height: "100vh",
      backgroundColor: "#f9f9f9",
      padding: "20px",
      fontFamily: "'Arial', sans-serif",
    },
    heading: {
      fontSize: "28px",
      fontWeight: "bold",
      color: "#333",
      marginBottom: "10px",
    },
    paragraph: {
      fontSize: "20px",
      color: "#555",
      marginBottom: "20px",
    },
    button: {
      padding: "12px 24px",
      fontSize: "16px",
      backgroundColor: "#6200EE",
      color: "#fff",
      border: "none",
      borderRadius: "6px",
      cursor: "pointer",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      transition: "background-color 0.3s",
    },
    buttonHover: {
      backgroundColor: "#4500b3",
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Checkout</h2>
      <p style={styles.paragraph}>Total: â‚¹{totalPrice}</p>
      <button
        style={styles.button}
        onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
        onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
      >
        Pay Now
      </button>
    </div>
  );
};

export default Checkout;
