import React, { useEffect, useState } from "react";
import "./Home.css";

import Login from "./Login";
import { apiUrl } from "../axios";
import axios from "axios";
import ListMedicines from "./";

export default function Home() {
  const [searchData, setSearchData] = useState({});
  const [categories, setCategories] = useState([]);
  const [formValues, setFormValues] = useState({});

  useEffect(() => {
    getCategories();
  }, []);

  const search = (e) => {
    e.preventDefault();
    setSearchData(formValues);
  };

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const getCategories = async () => {
    await axios
      .get(`${apiUrl}/medicine/categories`)
      .then((response) => {
        if (response.status === 200) {
          setCategories(response.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div id="page-top">
      <header className="masthead">
        <div className="container">
          <div className="masthead-subheading text-light">Medical Store</div>
          <div className="masthead-heading text-info">
            Your Health, Our Priority
          </div>
          {
            <a
              className="btn btn-danger btn-xl text-uppercase"
              href="#services"
            >
              Tell Me More
            </a>
          }
        </div>
      </header>
      <form onSubmit={search}>
        <div className="container mt-3 p-5" style={{ maxWidth: "60%" }}>
          <h5>Search Medicines</h5>
          <div className="row">
            <label htmlFor="medicine_name">Medicine Name</label>
            <div className="col">
              <input
                onChange={onChangeHandler}
                type="text"
                name="medicine_name"
                id="medicine_name"
                className="form-control"
                placeholder="Enter medicine name"
              />
            </div>
          </div>
          <div className="row mt-3">
            <label htmlFor="category">Category</label>
            <div className="col">
              <select
                onChange={onChangeHandler}
                name="category"
                className="form-control"
                id="category"
              >
                <option value="">SELECT</option>
                {categories.map((category, i) => {
                  return (
                    <option key={i} value={category.name}>
                      {category.name}
                    </option>
                  );
                })}
              </select>
            </div>
          </div>
          <div className="row mt-3">
            <div className="col">
              <button className="mt-3 btn btn-danger">Search</button>
            </div>
          </div>
        </div>
      </form>
      <section className="page-section" id="Medicines">
        <div className="container">
          <div className="text-center">
            <h2 className="section-heading text-uppercase">Medicines</h2>
            <h3 className="section-subheading text-muted">Browse Available Medicines</h3>
          </div>
          <div className="row">
            <ListMedicines searchData={searchData} />
          </div>
        </div>
      </section>
      <section className="page-section" id="services">
        <div className="container">
          <div className="text-center">
            <h2 className="section-heading text-uppercase">Services</h2>
            <h3 className="section-subheading text-muted">
              Our range of health services
            </h3>
          </div>
          <div className="row text-center">
            <div className="col-md-4">
              <span className="fa-stack fa-4x">
                <i className="fas fa-circle fa-stack-2x text-primary"></i>
                <i className="fas fa-shopping-cart fa-stack-1x fa-inverse"></i>
              </span>
              <h4 className="my-3">E-Commerce</h4>
              <p className="text-muted">
                Buy medicines online with ease, delivered to your doorstep.
              </p>
            </div>
            <div className="col-md-4">
              <span className="fa-stack fa-4x">
                <i className="fas fa-circle fa-stack-2x text-primary"></i>
                <i className="fas fa-laptop fa-stack-1x fa-inverse"></i>
              </span>
              <h4 className="my-3">Responsive Design</h4>
              <p className="text-muted">
                Access our store from any device, at any time, for a smooth experience.
              </p>
            </div>
            <div className="col-md-4">
              <span className="fa-stack fa-4x">
                <i className="fas fa-circle fa-stack-2x text-primary"></i>
                <i className="fas fa-lock fa-stack-1x fa-inverse"></i>
              </span>
              <h4 className="my-3">Web Security</h4>
              <p className="text-muted">
                Secure transactions and personal data protection for peace of mind.
              </p>
            </div>
          </div>
        </div>
      </section>
      <section className="page-section" id="about">
        <div className="container">
          <div className="text-center">
            <h2 className="section-heading text-uppercase">About</h2>
            <h3 className="section-subheading text-muted">
              Your Trusted Medical Store.
            </h3>
          </div>
        </div>
      </section>
    </div>
  );
}
