import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const stripePromise = loadStripe('your_stripe_publishable_key');

const PaymentForm = () => {
  const [clientSecret, setClientSecret] = useState('');
  const [amount, setAmount] = useState(0);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    street: '',
    city: '',
    state: '',
    zipCode: '',
  });
  const [editAddress, setEditAddress] = useState(false);

  const stripe = useStripe();
  const elements = useElements();

  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  useEffect(() => {
    setAmount(totalPrice * 100);
  }, [totalPrice]);

  useEffect(() => {
    const fetchClientSecret = async () => {
      const response = await fetch('http://localhost:8000/create-payment-intent/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount }),
      });

      if (response.ok) {
        const data = await response.json();
        setClientSecret(data.clientSecret);
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Failed to create payment intent');
      }
    };

    if (amount > 0) {
      fetchClientSecret();
    }
  }, [amount]);

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setAddress((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!stripe || !elements || !clientSecret) {
      return;
    }

    setProcessing(true);

    const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: address.fullName,
          address: {
            line1: address.street,
            city: address.city,
            state: address.state,
            postal_code: address.zipCode,
          },
        },
      },
    });

    if (stripeError) {
      setError(stripeError.message);
    } else if (paymentIntent.status === 'succeeded') {
      alert('Payment successful!');
    }
    setProcessing(false);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h3 style={styles.subHeading}>Billing Address</h3>
        {editAddress ? (
          <>
            <label style={styles.label}>Full Name</label>
            <input
              type="text"
              name="fullName"
              value={address.fullName}
              onChange={handleAddressChange}
              style={styles.input}
            />

            <label style={styles.label}>Street</label>
            <input
              type="text"
              name="street"
              value={address.street}
              onChange={handleAddressChange}
              style={styles.input}
            />

            <label style={styles.label}>City</label>
            <input
              type="text"
              name="city"
              value={address.city}
              onChange={handleAddressChange}
              style={styles.input}
            />

            <label style={styles.label}>State</label>
            <input
              type="text"
              name="state"
              value={address.state}
              onChange={handleAddressChange}
              style={styles.input}
            />

            <label style={styles.label}>Zip Code</label>
            <input
              type="text"
              name="zipCode"
              value={address.zipCode}
              onChange={handleAddressChange}
              style={styles.input}
            />

            <button
              type="button"
              onClick={() => setEditAddress(false)}
              style={{ ...styles.button, marginBottom: '20px' }}
            >
              Save Address
            </button>
          </>
        ) : (
          <div>
            <p><strong>Name:</strong> {address.fullName}</p>
            <p><strong>Street:</strong> {address.street}</p>
            <p><strong>City:</strong> {address.city}</p>
            <p><strong>State:</strong> {address.state}</p>
            <p><strong>Zip Code:</strong> {address.zipCode}</p>
            <button
              type="button"
              onClick={() => setEditAddress(true)}
              style={{ ...styles.button, marginBottom: '20px' }}
            >
              Edit Address
            </button>
          </div>
        )}

        <h3 style={styles.subHeading}>Payment Details</h3>
        <label style={styles.label}>Amount:</label>
        <input
          type="number"
          value={amount / 100}
          readOnly
          style={styles.input}
        />
        <CardElement style={{ base: { fontSize: '16px' } }} />

        <button
          type="submit"
          disabled={!stripe || processing || !clientSecret}
          style={
            processing || !stripe || !clientSecret
              ? { ...styles.button, ...styles.buttonDisabled }
              : styles.button
          }
        >
          {processing ? 'Processing...' : 'Pay'}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};

const PaymentPage = () => (
  <Elements stripe={stripePromise}>
    <PaymentForm />
  </Elements>
);

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  subHeading: {
    fontSize: '20px',
    marginBottom: '10px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
  form: {
    width: '100%',
  },
};

export default PaymentPage;
