import React from "react";
import { useLocation } from "react-router-dom";

const Booking = () => {
  const location = useLocation();
  const { order = {}, paymentDetails = {} } = location.state || {}; // Safeguard against undefined state

  const formatAmount = (amount) => (amount ? (amount / 100).toFixed(2) : "N/A");

  return (
    <div
      style={{
        padding: "20px",
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#f9f9f9",
        borderRadius: "8px",
        width: "80%",
        margin: "20px auto",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h2
        style={{
          fontSize: "24px",
          color: "#333",
          textAlign: "center",
          marginBottom: "20px",
        }}
      >
        Booking Details
      </h2>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 2fr",
          gap: "10px 20px",
          fontSize: "18px",
          color: "#555",
        }}
      >
        <div style={{ fontWeight: "bold" }}>Order ID:</div>
        <div>{order.id || "N/A"}</div>
        <div style={{ fontWeight: "bold" }}>Total Price:</div>
        <div>₹{formatAmount(order.amount)}</div>
        <h3
          style={{
            gridColumn: "1 / span 2",
            fontSize: "20px",
            color: "#444",
            margin: "20px 0 10px",
          }}
        >
          Payment Details:
        </h3>
        <div style={{ fontWeight: "bold" }}>Payment ID:</div>
        <div>{paymentDetails.razorpay_payment_id || "N/A"}</div>
        <div style={{ fontWeight: "bold" }}>Order ID:</div>
        <div>{paymentDetails.razorpay_order_id || "N/A"}</div>
        <div style={{ fontWeight: "bold" }}>Amount Paid:</div>
        <div>₹{formatAmount(paymentDetails.amount)}</div>
        <div style={{ fontWeight: "bold" }}>Signature:</div>
        <div>{paymentDetails.razorpay_signature || "N/A"}</div>
      </div>
    </div>
  );
};

export default Booking;
