import React, { useState } from "react";
import { useLocation } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const { cartItems = [], totalPrice = 0 } = location.state || {}; // Get passed data

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);

  const placeOrder = async () => {
    if (cartItems.length === 0) {
      alert("Your cart is empty!");
      return;
    }

    const orderData = {
      items: cartItems,
      total: totalPrice,
      paymentMethod: "Cash on delivery",
    };

    try {
      setIsSubmitting(true);
      // Simulate API call for placing an order
      console.log("Placing order:", orderData);
      setOrderSuccess(true);
    } catch (error) {
      console.error("Order submission failed:", error);
      alert("Failed to place the order. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Checkout</h1>
      {cartItems.length > 0 ? (
        <>
          <h4>Your Order</h4>
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                {item.medicine.name} - Quantity: {item.quantity} - Rs.{item.medicine.price}
              </li>
            ))}
          </ul>
          <h4>Total Price: Rs.{totalPrice}</h4>
          <button onClick={placeOrder} disabled={isSubmitting}>
            {isSubmitting ? "Placing Order..." : "Place Order"}
          </button>
        </>
      ) : (
        <p>No items in the cart.</p>
      )}
      {orderSuccess && <p style={{ color: "green" }}>Order placed successfully!</p>}
    </div>
  );
};

export default Checkout;
