import React, { useState } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { order, totalPrice } = location.state || {};
  const [paymentStatus, setPaymentStatus] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);

  const handlePayment = async () => {
    setIsProcessing(true);
    try {
      // Call payment processing endpoint
      const paymentResponse = await axios.post(
        "http://localhost:8000/medical_store_app/process_payment/",
        {
          order_id: order.id,
          amount: totalPrice,
          user_id: sessionStorage.getItem("user_id"),
        }
      );

      if (paymentResponse.data.success) {
        setPaymentStatus("Payment Successful!");

        // Clear cart after payment
        await axios.post("http://localhost:8000/medical_store_app/cart/clear", {
          user_id: sessionStorage.getItem("user_id"),
        });

        // Navigate to confirmation page
        navigate("/order-confirmation", { state: { order } });
      } else {
        setPaymentStatus("Payment Failed. Please try again.");
      }
    } catch (error) {
      console.error("Error during payment processing:", error);
      setPaymentStatus("An error occurred during payment.");
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <div style={styles.details}>
        <p style={styles.text}>Order ID: {order?.id}</p>
        <p style={styles.text}>Total Price: â‚¹{totalPrice}</p>
      </div>
      <button
        style={styles.paymentButton}
        onClick={handlePayment}
        disabled={isProcessing}
      >
        {isProcessing ? "Processing..." : "Pay Now"}
      </button>
      {paymentStatus && <p style={styles.paymentStatus}>{paymentStatus}</p>}
    </div>
  );
};

const styles = {
  container: {
    padding: "50px",
    fontFamily: "Arial, sans-serif",
    textAlign: "center",
  },
  heading: {
    fontSize: "2rem",
    marginBottom: "20px",
  },
  details: {
    marginBottom: "20px",
  },
  text: {
    fontSize: "1.2rem",
    marginBottom: "10px",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  paymentStatus: {
    marginTop: "20px",
    fontSize: "1.2rem",
    color: "red",
  },
};

export default Checkout;
