import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode'; // Corrected import
import { useNavigate } from 'react-router-dom';

const handleLogin = async (e) => {
  e.preventDefault();
  const endpoint = isAdmin ? 'http://localhost:8000/api/admin/login/' : 'http://localhost:8000/api/user/login/';
  
  console.log("Login Credentials:", credentials); // Add this line for debugging

  try {
    const response = await axios.post(endpoint, credentials, {
      headers: { 'Content-Type': 'application/json' },
    });

    const { access, refresh } = response.data;

    // Store tokens and handle user type
    sessionStorage.setItem('access_token', access);
    sessionStorage.setItem('refresh_token', refresh);

    const user = jwtDecode(access);
    const userType = user.user_type;

    axios.defaults.headers.common['Authorization'] = `Bearer ${access}`;

    if (isAdmin || userType === 'admin') {
      navigate('/admin/dashboard');
    } else {
      navigate('/user/dashboard');
    }
  } catch (error) {
    console.error("Login Error:", error); // Log the entire error object
    if (error.response) {
      console.log("Response data:", error.response.data); // Log response data
      console.log("Response status:", error.response.status); // Log response status
      if (error.response.status === 400) {
        console.log('Invalid username or password.');
      } else if (error.response.status === 401) {
        console.log('Unauthorized. Please check your credentials.');
      } else {
        console.log('Login failed. Please try again.');
      }
    } else if (error.request) {
      setError('No response from the server. Please check your network connection.');
    } else {
      setError('An unknown error occurred.');
    }
  }
};

export default Login;
