import React, { useState, useEffect } from 'react';
import AddProduct from './addproduct'; // Import AddProduct component

const Product = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('http://localhost:5000/api/products'); // Fetch products from the backend
      const data = await response.json();
      setProducts(data); // Assuming the response is an array of products
    };

    fetchProducts();
  }, []);

  const handleProductAdded = (newProduct) => {
    setProducts((prevProducts) => [...prevProducts, newProduct]); // Update the product list
  };

  return (
    <div>
      <AddProduct onProductAdded={handleProductAdded} /> {/* Pass handleProductAdded to AddProduct */}
      <h2>Product List</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>{product.name} - ${product.price}</li>
        ))}
      </ul>
    </div>
  );
};

export default Product;
