import React, { useEffect, useState } from "react";
import axios from "axios";

const MedicineBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:8000/medical_store_app/admin/bookings/");
        setBookings(response.data);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  const updateStatus = async (bookingId, status) => {
    try {
      await axios.post(`http://localhost:8000/medical_store_app/admin/bookings/${bookingId}/update/`, {
        status: status,
      });
      alert("Booking status updated!");
      setBookings(
        bookings.map((booking) =>
          booking.id === bookingId ? { ...booking, status } : booking
        )
      );
    } catch (error) {
      console.error("Error updating booking status:", error);
    }
  };
  
  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>Admin - Manage Bookings</h1>
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "20px" }}>
        <thead>
          <tr>
            <th style={headerStyle}>Customer</th>
            <th style={headerStyle}>Medicine</th>
            <th style={headerStyle}>Quantity</th>
            <th style={headerStyle}>Status</th>
            <th style={headerStyle}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.id}>
              <td style={cellStyle}>{booking.customer.username}</td>
              <td style={cellStyle}>{booking.product.name}</td>
              <td style={cellStyle}>{booking.quantity}</td>
              <td style={cellStyle}>{booking.status}</td>
              <td style={cellStyle}>
                <button
                  onClick={() => updateStatus(booking.id, "confirmed")}
                  style={buttonStyle("green")}
                >
                  Confirm
                </button>
                <button
                  onClick={() => updateStatus(booking.id, "rejected")}
                  style={buttonStyle("red")}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Inline styles
const headerStyle = {
  backgroundColor: "#f4f4f4",
  padding: "30px",
  textAlign: "left",
  fontWeight: "bold",
  border: "1px solid #ddd",
};

const cellStyle = {
  padding: "10px",
  border: "1px solid #ddd",
};

const buttonStyle = (color) => ({
  backgroundColor: color,
  color: "white",
  border: "none",
  padding: "5px 10px",
  margin: "0 5px",
  borderRadius: "5px",
  cursor: "pointer",
});

export default MedicineBookings;
