import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./Home.css";

// Define the base URL for API
const API_BASE_URL = "http://localhost:8000/medical_store_app/api";

export default function HomePage() {
  const [medicines, setMedicines] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState([]); // Cart state to hold added medicines

  useEffect(() => {
    fetchMedicines(); // Initially fetch medicines
  }, []);

  // Function to fetch all medicines
  const fetchMedicines = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/medicines/?search=cd`); // Replace 'cd' with an initial search term if needed
      setMedicines(response.data);
    } catch (error) {
      console.error("Error fetching medicines:", error);
    }
  };

  // Handle search input and search for medicines
  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    try {
      const response = await axios.get(`${API_BASE_URL}/medicines/?search=${searchTerm}`);
      setMedicines(response.data);
    } catch (error) {
      console.error("Error searching medicines:", error);
    }
  };

  const addToCart = (medicine) => {
    // Check if the user is logged in (e.g., check if token exists in localStorage)
    const isLoggedIn = localStorage.getItem("authToken") ? true : false;
    
    if (!isLoggedIn) {
      alert("You must be logged in to add items to the cart.");
      // Optionally, redirect to login page
      // window.location.href = "/login";
      return;
    }

    // Proceed with adding item to cart
    axios
      .post(`${API_BASE_URL}/cart/`, {
        medicine_id: medicine.id,
        quantity: 1,  // Modify as needed
      })
      .then((response) => {
        setCart((prevCart) => {
          const existingItem = prevCart.find((item) => item.id === response.data.id);
          if (existingItem) {
            return prevCart.map((item) =>
              item.id === existingItem.id ? { ...item, quantity: item.quantity + 1 } : item
            );
          }
          return [...prevCart, response.data];
        });
      })
      .catch((error) => {
        console.error("Error adding item to cart:", error);
      });
  };

  return (
    <div className="homepage">
      <header className="navbar">
        <h1>Medical Store</h1>
        <div className="nav-options">
          <Link to="/" className="nav-button">Home</Link>
          <Link to="/Cart" className="nav-button">Cart ({cart.length})</Link>
          <Link to="/Login" className="nav-button">Login/Register</Link>
        </div>
      </header>

      <section className="search-section">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search medicines..."
            className="search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="search-button" type="submit">Search</button>
        </form>
      </section>

      <main className="main-content">
        <h2>Welcome to Our Medical Store</h2>
        <p>Find the best medicines at affordable prices.</p>
        <div className="medicine-list">
          {medicines.map((medicine) => (
            <div key={medicine.id} className="medicine-item">
              <h3>{medicine.name}</h3>
              <p>{medicine.description}</p>
              <p>Price: ${medicine.price}</p>
              <button 
                className="add-to-cart-button"
                onClick={() => addToCart(medicine)}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>

        <section className="about-us">
          <h2>About Us</h2>
          <p>We are a trusted medical store offering a wide range of medicines to ensure your health and wellness. Our goal is to provide high-quality medicines at affordable prices for everyone.</p>
        </section>

        <section className="contact-us">
          <h2>Contact Us</h2>
          <p>If you have any questions, feel free to reach out to us:</p>
          <p>Email: support@medicalstore.com</p>
          <p>Phone: 123-456-7890</p>
          <p>Address: 123 Health St, Wellness City</p>
        </section>
      </main>

      <footer className="footer">
        <p>&copy; 2024 Medical Store. All rights reserved.</p>
      </footer>
    </div>
  );
}
