import axios from "axios";

const apiUrl = "http://localhost:8000"; // Make sure your Django server is running on this port

const axiosInstance = axios.create({
  baseURL: apiUrl,
  timeout: 5000,
  headers: {
    accept: "application/json",
  },
});

// Add a request interceptor to include the authorization header
axiosInstance.interceptors.request.use((config) => {
  const accessToken = sessionStorage.getItem("access_token");
  if (accessToken) {
    config.headers.Authorization = `Bearer ${accessToken}`;
  }
  return config;
});

// Function to generate a new refresh token if the current token is invalid
const generateRefreshToken = async (error) => {
  let refresh = false;
  if (error.response && error.response.status === 401 && !refresh) {
    refresh = true;
    try {
      const response = await axios.post(
        `${apiUrl}/api/token/refresh/`,
        {
          refresh: sessionStorage.getItem("refresh_token"),
        },
        { withCredentials: false }
      );

      if (response.status === 200) {
        // Update the access token
        sessionStorage.setItem("access_token", response.data.access);
        axiosInstance.defaults.headers["Authorization"] = `Bearer ${response.data.access}`;
        return axiosInstance(error.config); // Retry the original request
      }
    } catch (refreshError) {
      console.error("Refresh token error:", refreshError);
    }
  }
  
  return Promise.reject(error); // Reject if refresh fails
};

export { apiUrl, axiosInstance, generateRefreshToken };
