import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const PaymentForm = () => {
  const [amount, setAmount] = useState(0);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);

  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  useEffect(() => {
    if (totalPrice > 0) {
      setAmount(totalPrice * 100); // Convert to cents
    } else {
      setError('Invalid total price');
    }
  }, [totalPrice]);

  const handleLogin = async (event) => {
    event.preventDefault();
  
    try {
      const response = await fetch('http://localhost:8000/medical_store_app/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
  
      if (response.ok) {
        const data = await response.json();
        console.log('Login Response:', data); // Log the response
  
        const { access, refresh } = data; // These are your tokens
        if (!access || !refresh) {
          throw new Error('Missing tokens');
        }
  
        // Fetch user type from a separate endpoint, if not included in the login response
        const userDetailsResponse = await fetch('http://localhost:8000/medical_store_app/user-details/', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${access}`,
          },
        });
  
        if (userDetailsResponse.ok) {
          const userDetails = await userDetailsResponse.json();
          const { user_type } = userDetails;
          if (!user_type) {
            throw new Error('Missing user type');
          }
  
          // Save tokens and user info
          localStorage.setItem('auth_token', access);
          sessionStorage.setItem('user_type', user_type);
  
          // Navigate based on user type
          console.log('User Type:', user_type);
  
          if (user_type === 'admin') {
            alert('Admin login successfully!');
            navigate('/admin/User');
          } else if (user_type === 'user') {
            alert('User login successfully!');
            navigate('/UserProductViews');
          } else {
            alert('Unknown user type. Please contact support.');
            console.error('Received unknown user type:', user_type);
          }
        } else {
          throw new Error('Failed to fetch user details');
        }
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Login failed. Please try again.');
      }
    } catch (err) {
      console.error('Error during login:', err);
      setError('An error occurred. Please try again later.');
    }
  };
  
  

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      {error && <div style={styles.error}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label style={styles.label}>Amount</label>
          <input
            type="number"
            value={amount / 100}
            readOnly
            style={styles.input}
          />
        </div>
        <div>
          <button
            type="submit"
            style={processing ? { ...styles.button, ...styles.buttonDisabled } : styles.button}
            disabled={processing}
          >
            {processing ? 'Processing...' : 'Place Order'}
          </button>
        </div>
      </form>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
};

export default PaymentForm;
