import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const Booking = () => {
  const location = useLocation();
  const { order } = location.state || {};
  const [orderDetails, setOrderDetails] = useState(order || null);
  const [error, setError] = useState(null);
  const [status, setStatus] = useState(order?.status || "Pending"); // Default status

  useEffect(() => {
    if (!orderDetails) {
      // Fetch order details if not passed via navigation state
      const fetchOrderDetails = async () => {
        try {
          const response = await axios.get(
            `http://localhost:8000/medical_store_app/order_details/${order.id}/`
          );
          setOrderDetails(response.data.order);
          setStatus(response.data.order.status); // Set initial status from backend
        } catch (err) {
          setError("Failed to fetch order details.");
          console.error(err);
        }
      };

      fetchOrderDetails();
    }
  }, [orderDetails, order]);

  const updateStatus = async (newStatus) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/medical_store_app/order_update/${orderDetails.id}/`,
        { status: newStatus }
      );
      setStatus(response.data.status);
    } catch (err) {
      setError("Failed to update order status.");
      console.error(err);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Booking Confirmation</h1>
      {error ? (
        <p style={styles.error}>{error}</p>
      ) : orderDetails ? (
        <div style={styles.orderDetails}>
          <h2>Order Details:</h2>
          <p><strong>Order ID:</strong> {orderDetails.id}</p>
          <p><strong>Total Price:</strong> ${orderDetails.total_price}</p>
          <p><strong>Order Date:</strong> {orderDetails.date}</p>
          <p><strong>Status:</strong> {status}</p>
          <h3>Items:</h3>
          <ul>
            {orderDetails.items?.map((item) => (
              <li key={item.id}>
                {item.medicine_name} - {item.quantity} x ${item.price}
              </li>
            ))}
          </ul>
          <div style={styles.buttonContainer}>
            <button
              style={styles.confirmButton}
              onClick={() => updateStatus("Confirmed")}
            >
              Confirm
            </button>
            <button
              style={styles.rejectButton}
              onClick={() => updateStatus("Rejected")}
            >
              Reject
            </button>
          </div>
        </div>
      ) : (
        <p>Loading order details...</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "50px",
    fontFamily: "Arial, sans-serif",
  },
  orderDetails: {
    backgroundColor: "#f9f9f9",
    padding: "15px",
    borderRadius: "5px",
    maxWidth: "600px",
    margin: "0 auto",
  },
  error: {
    color: "red",
    fontWeight: "bold",
  },
  buttonContainer: {
    marginTop: "20px",
    display: "flex",
    justifyContent: "space-between",
  },
  confirmButton: {
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  rejectButton: {
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Booking;
