import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { axiosInstance } from "../axios"; // Import the axios instance

const Login = () => {
  const [formValues, setFormValues] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormValues({
      ...formValues,
      [e.target.name]: e.target.value,
    });
    setSuccess(null);
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axiosInstance.post("/api/token/", {
        username: formValues.username,
        password: formValues.password,
      });

      sessionStorage.setItem("access_token", response.data.access);
      sessionStorage.setItem("refresh_token", response.data.refresh);
      axiosInstance.defaults.headers["Authorization"] = "Bearer " + response.data.access;
      setSuccess("Login successful!");
      navigate("/DashBoard");
    } catch (error) {
      setError("Invalid username or password");
    } finally {
      setLoading(false);
    }
  };

  const handleSignOut = () => {
    sessionStorage.removeItem("access_token");
    sessionStorage.removeItem("refresh_token");
    delete axiosInstance.defaults.headers["Authorization"];
    navigate("/"); // Redirect to the login page or home page
  };

  // Inner CSS Styles
  const styles = {
    container: {
      height: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
    },
    card: {
      width: "450px",
      padding: "40px",
      backgroundColor: "#ffffff",
      borderRadius: "15px",
      boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)",
    },
    title: {
      fontSize: "30px",
      color: "#007bff",
      textAlign: "center",
      marginBottom: "20px",
    },
    formGroup: {
      marginBottom: "20px",
    },
    label: {
      display: "inline-block",
      width: "150px",
      textAlign: "right",
      marginRight: "10px",
      fontWeight: "bold",
    },
    input: {
      width: "calc(100% - 160px)",
      padding: "10px",
      border: "1px solid #ced4da",
      borderRadius: "4px",
      fontSize: "14px",
    },
    errorText: {
      fontSize: "14px",
      color: "#dc3545",
      marginBottom: "20px",
    },
    successText: {
      fontSize: "14px",
      color: "#28a745",
      marginBottom: "20px",
    },
    submitButton: {
      width: "50%",
      backgroundColor: "#007bff",
      border: "none",
      borderRadius: "8px",
      color: "white",
      padding: "10px",
      fontSize: "16px",
      cursor: "pointer",
    },
    submitButtonDisabled: {
      backgroundColor: "#6c757d", // Grey out button when loading
    },
    forgotPassword: {
      background: "none",
      border: "none",
      color: "#007bff",
      cursor: "pointer",
      textDecoration: "underline",
      marginTop: "20px",
    },
    signOutButton: {
      marginTop: "20px",
      width: "50%",
      backgroundColor: "#dc3545", // Red color for sign-out
      border: "none",
      borderRadius: "8px",
      color: "white",
      padding: "10px",
      fontSize: "16px",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <form onSubmit={handleSubmit}>
          <h2 style={styles.title}>Admin Login</h2>
          
          {/* Username */}
          <div style={styles.formGroup}>
            <label htmlFor="username" style={styles.label}>Username:</label>
            <input
              required
              type="text"
              name="username"
              id="username"
              placeholder="Enter your username"
              style={styles.input}
              onChange={handleChange}
            />
          </div>

          {/* Password */}
          <div style={styles.formGroup}>
            <label htmlFor="password" style={styles.label}>Password:</label>
            <input
              required
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              style={styles.input}
              onChange={handleChange}
            />
          </div>

          {/* Error message */}
          {error && <p style={styles.errorText}>{error}</p>}
          
          {/* Success message */}
          {success && <p style={styles.successText}>{success}</p>}

          {/* Submit button */}
          <div style={{ textAlign: "center" }}>
            <button 
              style={{
                ...styles.submitButton,
                ...(loading ? styles.submitButtonDisabled : {}),
              }} 
              disabled={loading}
            >
              {loading ? "Logging in..." : "Login"}
            </button>
          </div>
        </form>

        {/* Forgot Password Link */}
        <div style={{ textAlign: "center" }}>
          <button onClick={() => navigate("/ChangePassword")} style={styles.forgotPassword}>
            Forgot Password?
          </button>
        </div>

        {/* Sign Out Button */}
        <div style={{ textAlign: "center" }}>
          <button onClick={handleSignOut} style={styles.signOutButton}>
            Sign Out
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
