// Checkout.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "";

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    cvv: "",
    expiryDate: "",
  });
  const [address, setAddress] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }
        const cartResponse = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        if (cartResponse.data && cartResponse.data.cart_items) {
          setCartItems(cartResponse.data.cart_items);
          setTotalPrice(cartResponse.data.total_price);
        }

        const addressResponse = await axios.get(
          `http://localhost:8000/medical_store_app/get_address/${userId}`
        );
        if (addressResponse.data) {
          const { street, city, state, postal_code, phone_no } = addressResponse.data;
          const fullAddress = `${street}, ${city}, ${state} - ${postal_code}, ${phone_no}`;
          setAddress(fullAddress);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        setAddress("");
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
    if (e.target.value !== "Card") {
      setCardDetails({ cardNumber: "", cvv: "", expiryDate: "" });
    }
  };

  const handleCardDetailsChange = (e) => {
    const { name, value } = e.target;
    setCardDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handlePayment = async () => {
    if (!address.trim()) {
      alert("Please add your billing address.");
      return;
    }

    const userId = sessionStorage.getItem("user_id");
    if (!userId) {
      alert("Please log in.");
      navigate("/login");
      return;
    }

    const paymentData = {
      card_details: {
        card_number: cardDetails.cardNumber,
        expiration_date: cardDetails.expiryDate,
        cvv: cardDetails.cvv,
      },
    };

    try {
      let response;
      if (paymentType === "Card") {
        if (!cardDetails.cardNumber || !cardDetails.cvv || !cardDetails.expiryDate) {
          alert("Please fill in all card details.");
          return;
        }
        response = await axios.post("http://localhost:8000/medical_store_app/card-payment/", paymentData, {
          headers: {
            "Content-Type": "application/json",
          },
        });
      } else if (paymentType === "Cash") {
        response = await axios.post("http://localhost:8000/medical_store_app/cod-order/", paymentData, {
          headers: {
            "Content-Type": "application/json",
          },
        });
      }

      if (response && response.data.status === "success") {
        alert(response.data.message);
        navigate("/UserProductViews");
      } else {
        alert(response.data.error || "Payment failed");
      }
    } catch (error) {
      console.error("Payment error:", error);
      alert("An error occurred during payment.");
    }
  };

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>
      <div className="cart-items">
        <h3>Cart Items:</h3>
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              {item.name} - ${item.price} x {item.quantity}
            </li>
          ))}
        </ul>
        <p>Total Price: ${totalPrice}</p>
      </div>

      <div className="address-section">
        <h3>Delivery Address:</h3>
        <p>{address || "No address found. Please add your address."}</p>
      </div>

      <div className="payment-method">
        <h3>Payment Method:</h3>
        <label>
          <input
            type="radio"
            name="paymentType"
            value="Card"
            checked={paymentType === "Card"}
            onChange={handlePaymentTypeChange}
          />
          Credit/Debit Card
        </label>
        <label>
          <input
            type="radio"
            name="paymentType"
            value="Cash"
            checked={paymentType === "Cash"}
            onChange={handlePaymentTypeChange}
          />
          Cash on Delivery
        </label>
      </div>

      {paymentType === "Card" && (
        <div className="card-details">
          <h3>Card Details:</h3>
          <input
            type="text"
            name="cardNumber"
            placeholder="Card Number"
            value={cardDetails.cardNumber}
            onChange={handleCardDetailsChange}
          />
          <input
            type="text"
            name="cvv"
            placeholder="CVV"
            value={cardDetails.cvv}
            onChange={handleCardDetailsChange}
          />
          <input
            type="text"
            name="expiryDate"
            placeholder="Expiry Date"
            value={cardDetails.expiryDate}
            onChange={handleCardDetailsChange}
          />
        </div>
      )}

      <button className="payment-button" onClick={handlePayment}>
        Complete Payment
      </button>
    </div>
  );
};

export default Checkout;
